				where pokket_user_organization.organization_id=organizationId  and
                participant_referral.is_service_assigned_to_participant=1 
                and participant_referral.is_accepted is null and  
                participant_referral.is_onboarded =1 and ( participant.contract_id=@contractId or @viewerOrgId='acivilate')
        union
        select distinct pokket_user_id from pokket_user where pokket_user_id in (
				select pokket_user_id from participant_referral where
				case_manager_id in (select pokket_user_id from pokket_user_organization 
				where (organization_id=organizationId and (organizationId=@viewerOrgId or @viewerOrgId='acivilate') ) ) and is_service_assigned_to_participant=1)
   ) A )) B where char_length(roles)=char_length(B.roles);
   
  
     
   
   
END ;;
