CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetPrescreenQuestions`(
	participantPokketUserId varchar(50),
	inputLat float, 
    inputLng float, 
    needCategoryIds varchar(50),
    radius INT,
    createdBy varchar(50),
    awsRequestId varchar(50),
    includeVirtual tinyint(1),
    searchState varchar(2)
)
BEGIN
	SELECT TIMESTAMPDIFF(YEAR,STR_TO_DATE(birth_date, '%m/%d/%Y'),CURDATE()),
		gender,registered_as_sex_offender, is_veteran INTO @age,@gender,@sexOffender, @veteran FROM `participant`
        WHERE `participant`.`pokket_user_id`=participantPokketUserId;
        
	IF @veteran = 'Yes' THEN
		SET @participant_is_veteran = 1;
	ELSE
		SET @participant_is_veteran = 0;
    END IF;

	SET @Query = CONCAT('SELECT DISTINCT ifnull(prescreen_question_id,0) as prescreenQuestionId,
    B.organizationServiceId as serviceId,
    ifnull(prescreen_question_description,',QUOTE('NA'),') as description,
    ifnull(prescreen_question_qualifying_answer,',QUOTE('NA'),') as qualifyingAnswer
	from `prescreen_question` inner join  
    (
	SELECT distinct organizationServiceId FROM 
    (SELECT DISTINCT
    round(69*vincenty(`facility_latitude`,`facility_longitude`,',inputLat,',', inputLng,'),2) as distance,
	`facility_service`.`service_id` as organizationServiceId,
    `organization_service`.`service_area` as serviceArea,
    B.serviceStates as serviceStates
	FROM 
 `facility_service` inner join
 `organization_facility` on 
 `facility_service`.`facility_id`=`organization_facility`.`organization_facility_id`
 inner join
 `organization_service` on 
 `facility_service`.`service_id`=`organization_service`.`organization_service_id`
 inner join `ref_need_category` 
 on `ref_need_category`.`need_category_id`=`organization_service`.`need_category_id`
 left join  (select service_id, GROUP_CONCAT(DISTINCT `service_state`.`state_cd`) AS serviceStates
	from service_state WHERE `service_state`.`is_active` =1 group by service_id) B on
    `organization_service`.`organization_service_id` = B.`service_id`
 WHERE organization_facility.`facility_status_id`=2 and organization_service.`service_status_id`=2 
 and organization_facility.is_virtual <= ',includeVirtual,'
 and organization_service.veteran_only <= ',@participant_is_veteran,'
 and `organization_service`.`organization_service_id` not in (select service_id from participant_referral where
 (is_service_assigned_to_participant=1 or can_submit_referral_after_discharge=0 OR referral_status_id in (1,3)) 
 and pokket_user_id=',QUOTE(participantPokketUserId),')
 and FIND_IN_SET(`organization_service`.`need_category_id`,', QUOTE(needCategoryIds),') 
  and ',@age,' between `organization_service`.`service_eligibility_min_age` and `organization_service`.`service_eligibility_max_age`
  and (`organization_service`.`service_eligibility_gender_id` in (4,5,',@gender,') ) ');
   if @sexOffender=1 then
		SET @Query = CONCAT(@Query, ' and `organization_service`.`is_available_for_sex_offender`=1');
	end if;	

  SET @Query = CONCAT(@Query, ' and `organization_service`.`service_status_id`=2
 ) A
 where distance <=',radius,' 
 or (1 = ',includeVirtual,' and serviceArea = ',QUOTE('NATIONWIDE'),')
 or (1 = ',includeVirtual,' and serviceArea = ',QUOTE('STATEWIDE'),' and INSTR(serviceStates,',QUOTE(searchState),')>0)
 ) B
 on `prescreen_question`.`organization_service_id`=B.organizationServiceId
 order by organization_service_id');
 

 PREPARE stmt FROM @Query;
EXECUTE stmt;

	

    

END ;;
