CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetRecurringEvents`(
		pokketUserId varchar(50),
        start_date datetime)
BEGIN
  
	set @n:=0;
    
    SELECT `pokket_user_event`.`event_id`,
    `pokket_user_event`.`pokket_user_id`,
    `pokket_user_event`.`event_description`,
    `pokket_user_event`.`event_tz`,
    `pokket_user_event`.`ends_at_dtm`,
    `pokket_user_event`.`event_repeat_interval`,
    `pokket_user_event`.`event_repeat_type_id`,
    `pokket_user_event`.`is_active`,
    convert_tz(`pokket_user_event`.`starts_at_dtm`,'GMT',`pokket_user_event`.`event_tz`) as eventDate,
     0 as n
	 FROM `mypokket`.`pokket_user_event`
	 WHERE `pokket_user_event`.`event_repeat_interval`=0 AND 
	 `pokket_user_event`.`is_active`=1 AND
     `pokket_user_event`.`starts_at_dtm` >= start_date
     union
     SELECT `pokket_user_event`.`event_id`,
    `pokket_user_event`.`pokket_user_id`,
    `pokket_user_event`.`event_description`,
    `pokket_user_event`.`event_tz`,
    `pokket_user_event`.`ends_at_dtm`,
    `pokket_user_event`.`event_repeat_interval`,
    `pokket_user_event`.`event_repeat_type_id`,
    `pokket_user_event`.`is_active`,
     convert_tz(`pokket_user_event`.`starts_at_dtm`,'GMT',`pokket_user_event`.`event_tz`) + INTERVAL @n day as eventDate, 
     (select @n:= @n+`pokket_user_event`.`event_repeat_interval`) as n
 	from ref_number_series, 
	`mypokket`.`pokket_user_event`
	 WHERE `pokket_user_event`.`event_repeat_interval` != 0 AND 
	 `pokket_user_event`.`is_active`=1 AND
     `pokket_user_event`.`starts_at_dtm` >= start_date
     order by  eventDate;
     


    
  
    
    
    

    

END ;;
