CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetReferralDocuments`(
		participantPokketUserId varchar(50), facilityId varchar(50), serviceId varchar(50),
        awsRequestId varchar(50))
BEGIN

	select participant_referral_id into @participantReferralId from participant_referral where
    pokket_user_id=participantPokketUserId and facility_id=facilityId and service_id=serviceId;
    
    SELECT `document`.`document_id` as documentId,
    `plan_item_document`.`participant_plan_item_id` AS  planItemId,
	`plan_item_document`.`recurrence_id` AS  recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
	`plan_item_document`.`service_plan_item_document_id` as servicePlanItemDocumentId,
    `participant_plan_item`.`need_category_id` as needCategory,
	`participant_plan_item`.`pokket_user_id` as pokketUserId,
     concat(`pokket_user`.`first_name`,' ',`pokket_user`.`last_name`) as uploadedBy,
     DATE_FORMAT(`document`.`created_dtm`,'%m/%d/%Y %r') as createdDateTime
	FROM `participant_referral_document`
    JOIN `document` ON `participant_referral_document`.`document_id` = `document`.`document_id` 
    left join `plan_item_document`
    on `document`.`document_id`=`plan_item_document`.`document_id`
    inner join `participant_plan_item`
    on `plan_item_document`.`participant_plan_item_id`=`participant_plan_item`.`participant_plan_item_id`
    inner join `pokket_user` on
    `participant_plan_item`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
	WHERE `participant_referral_document`.`referral_id` = @participantReferralId
    AND `document`.`is_active`=1
    UNION
    SELECT `document`.`document_id` as documentId,
     null AS  planItemId,
     null AS  recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
     null as servicePlanItemDocumentId,
    `pokket_user_document`.`need_category_id` as needCategory,
     pokket_user_document.pokket_user_id as pokketUserId,
     concat(`pokket_user`.`first_name`,' ',`pokket_user`.`last_name`) as uploadedBy,
    DATE_FORMAT(`document`.`created_dtm`,'%m/%d/%Y %r') as createdDateTime
    FROM `participant_referral_document`
    JOIN `document` ON `participant_referral_document`.`document_id` = `document`.`document_id`
    left join `pokket_user_document`
    on `document`.`document_id`=`pokket_user_document`.`document_id`
    inner join `pokket_user` on
    `pokket_user_document`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
	WHERE `participant_referral_document`.`referral_id` = @participantReferralId
    AND `document`.`is_active`=1;
	
END ;;
