CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetService`(
		serviceId varchar(50),awsRequestId varchar(50))
BEGIN
 
	SELECT `organization_service`.`organization_service_id` as serviceId,
    `organization_service`.`organization_id` as orgId,
    `organization_onboarding`.`onboarded_by_org_id` as onboardedByOrgId,
    organization.organization_name as orgName,
    `organization_service`.`service_name` as name,
    `organization_service`.`service_status_id` as status,
    `organization_service`.`need_category_id` as needCategory,
    `organization_service`.`service_eligibility_min_age` as qualifyingMinAge,
    `organization_service`.`service_eligibility_max_age` as qualifyingMaxAge,
    `organization_service`.`is_referral_required` as referralRequired,
    `organization_service`.`service_eligibility_gender_id` as genderQualifies,
    `organization_service`.`is_available_for_sex_offender` as sexOffenderQualifies,
    `organization_service`.`is_available_for_family_children` as childQualifies,
    `organization_service`.`release_of_information_text` as roiText,
	`organization_service`.`referral_doc_requirement` as referralDocumentation,
     `organization_service`.`referral_doc_desc` as referralDocumentationDescription,
     GROUP_CONCAT(`A`.`organization_facility_id`) AS facilityIds,
     GROUP_CONCAT(`A`.`facility_name`) AS facilityNames,
     GROUP_CONCAT(`organization_service_payscale`.`pay_scale_id`) as payScales,
     GROUP_CONCAT(`service_intake_question`.`service_intake_question_description`) as serviceIntakeQuestions,
	 GROUP_CONCAT(`prescreen_question`.`prescreen_question_description`) as prescreenQuestions,
     `organization_service`.`service_type` as serviceType,
     `organization_service`.`service_area` as serviceArea,
     `organization_service`.`is_searchable` as isSearchable,
     `organization_service`.`service_description` as serviceDescription,
     `organization_service`.`service_contact_info` as serviceContactInfo,
     GROUP_CONCAT(DISTINCT `service_state`.`state_cd`) AS serviceStates,
     `organization_service`.`veteran_only` as veteranOnly
  	FROM `organization_service` inner join organization
    on organization_service.organization_id=organization.organization_id
    left join organization_onboarding on 
    organization.organization_id = organization_onboarding.organization_id
    left join `organization_service_payscale`
    on `organization_service`.`organization_service_id`=
    `organization_service_payscale`.`organization_service_id`
    left join `service_intake_question` on 
    `organization_service`.`organization_service_id`=
    `service_intake_question`.`organization_service_id`
    left join `prescreen_question` on
    `organization_service`.`organization_service_id`=
    `prescreen_question`.`organization_service_id`
     left join 
    (select `organization_facility_id`,`facility_name`,
    `service_id`
    from `organization_facility` inner join 
    `facility_service` on `organization_facility`.`organization_facility_id`=
    `facility_service`.`facility_id` where `facility_service`.`service_id`=
    serviceId) `A`
    on `organization_service`.`organization_service_id`=`A`.`service_id`
    left join `service_state` on
    (`organization_service`.`organization_service_id` = `service_state`.`service_id` 
    AND 1 = `service_state`.`is_active`)
    where `organization_service`.`organization_service_id`=serviceId;


END ;;
