CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetServiceOrganization`(
		serviceId varchar(50),awsRequestId varchar(50))
BEGIN

SELECT `organization_service`.`organization_id` into @organizationId 
		FROM `organization_service` where 
		`organization_service`.`organization_service_id`=serviceId;

	select  if(count(*)>0,1,0) into @hasServices from vw_active_organization_services where organization_id=@organizationId;
    
    select  if(count(*)>0,1,0) into @hasFacilites from vw_active_organization_facilities where organization_id=@organizationId;
    
    select if(count(*)>0,1,0) into @hasServicesWithFacilities from vw_active_organization_services
	inner join facility_service 
	on vw_active_organization_services.organization_service_id=facility_service.service_id
	inner join vw_active_organization_facilities 
	on facility_service.facility_id=vw_active_organization_facilities.organization_facility_id
	where vw_active_organization_services.organization_id=@organizationId;
    
    select if(count(*)>0,1,0) into@hasServicesWithCMs  from 
	vw_active_organization_services inner join facility_service_case_manager 
	on vw_active_organization_services.organization_service_id= facility_service_case_manager.service_id
	inner join vw_active_pokket_users 
	on facility_service_case_manager.pokket_user_id = vw_active_pokket_users.pokket_user_id
	where facility_service_case_manager.is_active=1 and vw_active_organization_services.organization_id=@organizationId;
    
     select if(count(*)>0,1,0) into@hasFacilitiesWithCMs  from 
	vw_active_organization_facilities inner join facility_case_manager 
	on vw_active_organization_facilities.organization_facility_id= facility_case_manager.facility_id
	inner join vw_active_pokket_users 
	on facility_case_manager.pokket_user_id = vw_active_pokket_users.pokket_user_id
	where facility_case_manager.is_active=1 and vw_active_organization_facilities.organization_id=@organizationId;

 
	 SELECT `organization`.`organization_id` as organizationId,
    `organization`.`organization_name` as name,
    `organization`.`organization_type_id`as orgType,
    `organization`.`organization_status_id` as status,
    `organization`.`jurisdiction_code` as jurisdictionCode,
    `organization`.`contract_id` as contractId,
	 @hasServices as hasServices,
     @hasFacilites as hasFacilites,
     @hasServicesWithFacilities as hasServicesWithFacilities,
     @hasServicesWithCMs as hasServicesWithCMs,
     @hasFacilitiesWithCMs as hasFacilitiesWithCMs
  FROM `organization` 
     where `organization`.`organization_id`= @organizationId;


	


    

END ;;
