   node.organization_status_id as status,
    node.jurisdiction_code as jurisdictionCode,
node.is_lead_agency as isLeadAgency,
    node.is_cja as isCriminalJusticeAgency,
	node.is_non_participating as isNonParticipating,
    `organization_onboarding`.`sp_invite_status_id` as spInviteStatus,
    GROUP_CONCAT(`organization_role`.`org_role_id`) AS roles,
    spaCount, cmCount, participantCount ,spaCmCount ,serviceProvidersCount
    FROM organization AS parent,
   organization AS node
        left join 
     `organization_onboarding` on
     node.organization_id=
     `organization_onboarding`.`organization_id`
     left join 
     `organization_role` on
     node.organization_id=
     `organization_role`.`organization_id`
     left join (SELECT pokket_user_organization.organization_id,
     SUM(CASE WHEN user_roles='SPA' THEN 1 ELSE 0 END) AS spaCount
    ,SUM(CASE WHEN user_roles ='CM' THEN 1 ELSE 0 END) AS cmCount
    ,SUM(CASE WHEN user_roles ='SPA/CM' THEN 1 ELSE 0 END) AS spaCmCount
FROM pokket_user_organization
        left join vw_pokket_user_role
        on pokket_user_organization.pokket_user_id= vw_pokket_user_role.pokket_user_id
        inner join pokket_user on  vw_pokket_user_role.pokket_user_id=pokket_user.pokket_user_id
        where pokket_user.pokket_user_status_id !=6 
        group by organization_id) A
        on
     node.organization_id=A.organization_id
     left join (select onboarded_by_org_id, count(*) as serviceProvidersCount from organization_onboarding inner join organization
							on organization_onboarding.organization_id = organization.organization_id
							where  sp_invite_status_id in (3,6)
							and organization.organization_status_id !=6
							and organization.organization_type_id=2
                            and  organization.lft BETWEEN @lft AND @rgt
							group by onboarded_by_org_id) Z
     on node.organization_id = Z.onboarded_by_org_id
     left join (     
				select count(*) as participantCount, organization_id from (
     
        select participant_referral.pokket_user_id, pokket_user_organization.organization_id
        from participant_referral inner join pokket_user_organization
				on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
                inner join pokket_user on 
                participant_referral.pokket_user_id= pokket_user.pokket_user_id
				where pokket_user_status_id !=6
                and (organizationId = @viewerOrgId or @viewerOrgId='acivilate') and 
                participant_referral.is_service_assigned_to_participant=1 
                and participant_referral.is_accepted=1 and  
                participant_referral.is_onboarded is null
		union
         
			 select participant_referral.pokket_user_id, pokket_user_organization.organization_id
             from participant_referral inner join pokket_user_organization
							on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
                             inner join pokket_user on 
							participant_referral.pokket_user_id= pokket_user.pokket_user_id
							where pokket_user_status_id !=6 and
							participant_referral.is_service_assigned_to_participant=1 
							and participant_referral.is_accepted=1 and  
							participant_referral.is_onboarded is null and 
							participant_referral.pokket_user_id in (
									select participant_referral.pokket_user_id from participant_referral inner join 
									participant on participant_referral.pokket_user_id=participant.pokket_user_id
									inner join pokket_user_organization
									on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
									where pokket_user_organization.organization_id=@viewerOrgId and
									participant_referral.is_service_assigned_to_participant=1 
									and participant_referral.is_accepted is null and  
									participant_referral.is_onboarded =1 and ( participant.contract_id=@contractId or @viewerOrgId='acivilate'))
        union
        
        select participant_referral.pokket_user_id, pokket_user_organization.organization_id
        from participant_referral inner join 
        participant on participant_referral.pokket_user_id=participant.pokket_user_id
        inner join pokket_user_organization
				on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
                inner join pokket_user on 
							participant_referral.pokket_user_id= pokket_user.pokket_user_id
							where pokket_user_status_id !=6 and 
                participant_referral.is_service_assigned_to_participant=1 
                and participant_referral.is_accepted is null and  
                participant_referral.is_onboarded =1 and ( participant.contract_id=@contractId or @viewerOrgId='acivilate')
                union
        
        
        select participant_referral.pokket_user_id,pokket_user_organization.organization_id from participant_referral inner join 
        participant on participant_referral.pokket_user_id=participant.pokket_user_id
        inner join pokket_user_organization
				on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
                inner join pokket_user on 
							participant_referral.pokket_user_id= pokket_user.pokket_user_id
							where pokket_user_status_id !=6 and 
				  participant_referral.is_accepted is null
                 and participant_referral.is_discharged=1
                and participant_referral.is_onboarded =1 and ( organization_id=@viewerOrgId or @viewerOrgId='acivilate')        
        union
		select participant_referral.pokket_user_id, @viewerOrgId from participant_referral 
                inner join pokket_user on 
							participant_referral.pokket_user_id= pokket_user.pokket_user_id
							where pokket_user_status_id !=6 and
				case_manager_id in (select pokket_user_id from pokket_user_organization 
				where  (organizationId=@viewerOrgId or @viewerOrgId='acivilate')  ) 
                and is_service_assigned_to_participant=1) P group by P.organization_id ) PC 
                on node.organization_id=PC.organization_id
     where node.organization_type_id=2
     and node.organization_status_id !=6 
        and  node.lft BETWEEN parent.lft AND parent.rgt
and parent.organization_id !=node.organization_id
 and node.lft !=0 and node.rgt !=0 and parent.lft !=0 and parent.rgt !=0 
AND parent.organization_id = organizationId
and `organization_onboarding`.`onboarded_by_org_id`=organizationId
group by node.organization_id
ORDER BY parent.lft;
 



	


    

END ;;
