CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetSharedDocuments`(
		pokketUserId varchar(50), serviceId varchar(50),viewerPokketUserId varchar(50),
        awsRequestId varchar(50))
BEGIN

	SET @serviceIds =serviceId;
    if serviceId is null then
		select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=viewerPokketUserId 
		group by pokket_user_id) AS A;
    end if;

	select count(*) into @countOfActiveWholePlanShares from vw_active_pokket_user_shares 
            where share_type_id=1 and shared_by=pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
	select count(*) into @countOfActiveEntireFileCabinetShares from vw_active_pokket_user_shares 
            where share_type_id=5 and shared_by=pokketUserId and FIND_IN_SET(shared_with,@serviceIds);
            
    SET @Query = CONCAT('SELECT `document`.`document_id` as documentId,
    `plan_item_document`.`participant_plan_item_id` as planItemId,
    `plan_item_document`.`recurrence_id` as recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
    `plan_item_document`.`service_plan_item_document_id` as servicePlanItemDocumentId,
    `participant_plan_item`.`need_category_id` as needCategory,',
    QUOTE(pokketUserId),' as pokketUserId,
    concat(`pokket_user`.`first_name`,',QUOTE(' '),',`pokket_user`.`last_name`) as uploadedBy,
    DATE_FORMAT(`document`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as createdDateTime,
    `document`.`created_dtm`
	FROM `document` inner join `plan_item_document`
    on `document`.`document_id`=`plan_item_document`.`document_id`
    INNER JOIN `participant_plan_item` on
    `participant_plan_item`.`participant_plan_item_id`=
    `plan_item_document`.`participant_plan_item_id`
    inner join `pokket_user` on
    `participant_plan_item`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
	WHERE `document`.`is_active`=1 AND
	`participant_plan_item`.`pokket_user_id`=',QUOTE(pokketUserId));
    
    IF @serviceIds IS NOT NULL THEN
		SET @Query = CONCAT(@Query, '  and find_in_set( `document`.`document_id`, getSharedDocumentIds(',QUOTE(pokketUserId),',', QUOTE(viewerPokketUserId),',',@countOfActiveWholePlanShares,',',@countOfActiveEntireFileCabinetShares,' ))');
	END IF;
    
    SET @Query = CONCAT(@Query, ' UNION
    SELECT `document`.`document_id` as documentId,
     null AS  planItemId,
     null AS  recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
     null as servicePlanItemDocumentId,
    `pokket_user_document`.`need_category_id` as needCategory,',
     QUOTE(pokketUserId),' as pokketUserId,
     concat(`pokket_user`.`first_name`,',QUOTE(' '),',`pokket_user`.`last_name`) as uploadedBy,
    DATE_FORMAT(`document`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as createdDateTime,
    `document`.`created_dtm`
	FROM `document` inner join `pokket_user_document`
    on `document`.`document_id`=`pokket_user_document`.`document_id`
    inner join `pokket_user` on
    `pokket_user_document`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
	WHERE `document`.`is_active`=1 AND 
	`pokket_user_document`.`pokket_user_id`=', QUOTE(pokketUserId));
    SET @Query = CONCAT(@Query, ' AND  `document`.`document_id` IN ( 
    SELECT shared_ref_id from vw_active_pokket_user_shares where shared_by=',QUOTE(pokketUserId), ' AND
    share_type_id=4');
    IF @serviceIds IS NOT NULL THEN
		SET @Query = CONCAT(@Query, '  and FIND_IN_SET(shared_with,',QUOTE(@serviceIds),')');
	END IF;
    SET @Query = CONCAT(@Query, ' )');
    
    
	IF @countOfActiveEntireFileCabinetShares > 0 THEN
		SET @Query = CONCAT(@Query, ' UNION
			SELECT `document`.`document_id` as documentId,
			 null AS planItemId,
			 null AS recurrenceId,
			`document`.`document_name` as `name`,
			`document`.`document_url` as url,
			`document`.`is_active` as isActive,
			`document`.`is_scanned` as isScanned,
			`document`.`is_quarantined` as isQuarantined,
			`document`.`antivirus_scan_status` AS `antivirusScanStatus`,
			 null as servicePlanItemDocumentId,
			`pokket_user_document`.`need_category_id` as needCategory,',
			 QUOTE(pokketUserId),' as pokketUserId,
			 concat(`pokket_user`.`first_name`,',QUOTE(' '),',`pokket_user`.`last_name`) as uploadedBy,
			DATE_FORMAT(`document`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as createdDateTime,
			`document`.`created_dtm`
			FROM `document` 
            inner join `pokket_user_document`
				on `document`.`document_id`=`pokket_user_document`.`document_id`
			inner join `pokket_user` on
				`pokket_user_document`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
			WHERE `document`.`is_active`=1 AND 
				`pokket_user_document`.`doc_category_id` != 2 AND
				`pokket_user_document`.`pokket_user_id`=', QUOTE(pokketUserId));
			IF @serviceIds IS NOT NULL THEN
            
				SET @Query = CONCAT(@Query, ' AND  `document`.`document_id` NOT IN ( 
				SELECT shared_ref_id from pokket_user_share where shared_by=',QUOTE(pokketUserId), ' AND
				share_type_id=4 AND is_active=0 and FIND_IN_SET(shared_with,',QUOTE(@serviceIds),')');
			END IF;
			SET @Query = CONCAT(@Query, ' )');
	END IF;
    
    SET @Query = CONCAT(@Query, ' order by 15 desc');
  
     PREPARE stmt FROM @Query;
	 EXECUTE stmt;
END ;;
