CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetUnreadCountsForSms`(

		pokketUserId varchar(50), awsRequestId varchar(50))
BEGIN



SET @smsOptIn =0;

SET @primaryPhoneNumber = null;

SET @isDependent = 0;





select primary_phone_number, is_sms_opt_in into @primaryPhoneNumber, @smsOptIn from pokket_user where

pokket_user_id=pokketUserId;



select is_dependent into @isDependent from participant where

pokket_user_id=pokketUserId;



if @isDependent = 1 then

	select primary_phone_number, is_sms_opt_in into @primaryPhoneNumber, @smsOptIn from pokket_user where

	pokket_user_id in (select primary_guardian_id from participant where pokket_user_id=pokketUserId);

end if;





if @isDependent = 1 then 



select pokket_user.pokket_user_id, @primaryPhoneNumber as primary_phone_number, ifnull(CountOfUnreadNotifications,0) as notification_count,

CONCAT(ifnull(CountOfUnreadNotifications,0) ,' ', 

if (ifnull(CountOfUnreadNotifications,0) > 0, 'notifications','notification')) as notification_count_suffix,

if(length(senders)=2,senders,concat(substr(senders,instr(senders,',')+1),' and ', left(senders,2))) as senders,

ifnull(CountOfUnreadMessages,0) as message_count,

