CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetUnreadMessageCount`(
		pokketUserId varchar(50),viewerPokketUserId varchar(50),awsRequestId varchar(50))
BEGIN

select count(*) as CountOfUnreadMessages from 
pokket_user_message inner join message on pokket_user_message.message_id=message.message_id
inner join pokket_user_message_thread on 
message.message_thread_id=pokket_user_message_thread.message_thread_id 
and pokket_user_message.pokket_user_id=pokket_user_message_thread.pokket_user_id 
inner join message_thread 
on pokket_user_message_thread.message_thread_id = message_thread.message_thread_id
left join (
	 select distinct A.service_id as serviceId from 
         (select service_id from facility_service_case_manager where pokket_user_id=pokketUserId and is_active=1 
				union
			select service_id  from participant_referral where  is_service_assigned_to_participant=1 
			 and pokket_user_id= pokketUserId
             )A
             INNER JOIN 
             ( select service_id from facility_service_case_manager where pokket_user_id=viewerPokketUserId and is_active=1  
				union
			select service_id  from participant_referral where  is_service_assigned_to_participant=1 
			 and pokket_user_id= viewerPokketUserId
             )B
             on A.service_id=B.service_id
	) SERV on
    pokket_user_message_thread.service_id=SERV.serviceId
where pokket_user_message.pokket_user_id=pokketUserId and
 pokket_user_message.is_read=0 and pokket_user_message.message_direction='TO' 
 and message_thread.is_active=1;
 

    
    
    
    
    

    

