CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetUnreadNotificationsAndMessages`(
		notifyInterval int(11),awsRequestId varchar(50))
BEGIN
select distinct email_address, CountOfUnreadNotifications, CountOfUnreadMessages from (    
select     X.email_address,CountOfUnreadNotifications,CountOfUnreadMessages, ifnull(already_notified,0) as already_notified  from (
(select email_address,ifnull(CountOfUnreadNotifications,0) as CountOfUnreadNotifications,ifnull(CountOfUnreadMessages,0) as CountOfUnreadMessages
from pokket_user 
left join 
(select pokket_user_id,count(*) as CountOfUnreadNotifications from notification
where is_read=0

group by pokket_user_id) A
on pokket_user.pokket_user_id=A.pokket_user_id
left join
(select pokket_user_id,count(*) as CountOfUnreadMessages from pokket_user_message
where is_read=0

group by pokket_user_id) B
on pokket_user.pokket_user_id=B.pokket_user_id
where email_address is not null 
and pokket_user_status_id=4 
and email_address !=''
and email_address not in (select email_address from sent_email where is_bad_address=1)
and notify_interval=notifyInterval
and (CountOfUnreadNotifications is not null 
or CountOfUnreadMessages is not null)) X
left join (select distinct sent_email.email_address, if(TIMESTAMPDIFF(DAY, DATE_FORMAT(NOW(),"%Y-%m-%d 00:00:00"),IFNULL(max_created_dtm,'1900-01-01 00:00:00'))<0,0,1) as already_notified from 
sent_email inner join (select email_address, max(created_dtm) as max_created_dtm from sent_email group by email_address) M
on sent_email.email_address = M.email_address
where is_bad_address <> 1) Y
on X.email_address=Y.email_address
) 
where already_notified=0) Z;

    

     
     
     


    
    
    

    

END ;;
