CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspInsertAppointmentRequest`(
		appointmentRequestId varchar(50),
        fromPokketUserId varchar(50),
        toPokketUserId varchar(50),
        meetingPurpose varchar(250),
        needCategoryId tinyint(4),
        serviceId varchar(50),
        address varchar(250),
        location varchar(250),
        videoMeetingId varchar(1000),
        requestNote varchar(1000),
        timeSlots varchar(100),
        notificationBatchId varchar(50),
        isWithAssignedCm tinyint(1),
        parentAppointmentRequestId varchar(50),
        createdBy varchar(50),
        awsRequestId varchar(50),
        duration smallint(4),
        appointmentBatchId varchar(50),
        hideParticipantsMode tinyint(1))
BEGIN

    DECLARE formattedTimeSlots varchar(250);
    DECLARE timeSlotPreference tinyint(1);
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    SET @appointmentRequestStatusId = 1;
    
    if appointmentRequestId = appointmentBatchId THEN
		SET @appointmentRequestStatusId = 2;
	END IF;

    INSERT INTO `appointment_request`
		(`appointment_request_id`,
		`from_pokket_user_id`,
		`to_pokket_user_id`,
		`meeting_purpose`,
        `need_category_id`,
        `service_id`,
        `location`,
        `address`,
        `request_note`,
		`appointment_request_status_id`,
        `is_with_assigned_cm`,
        `parent_appt_request_id`,
		`created_by`,
		`created_dtm`,
        `aws_request_id`,
        `duration_in_minutes`,
        `video_meeting_id`,
        `appointment_batch_id`)
		VALUES
		(appointmentRequestId,
		fromPokketUserId,
		toPokketUserId,
		meetingPurpose,
        needCategoryId,
        serviceId,
        location,
        address,
        requestNote,
		@appointmentRequestStatusId,
        isWithAssignedCm,
        parentAppointmentRequestId,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId,
        duration,
        videoMeetingId,
        appointmentBatchId);
        
	INSERT INTO `appointment_settings`
		(`appointment_request_id`,
		`hide_participants_mode`,
		`created_by`,
		`created_dtm`,
        `aws_request_id`)
		VALUES
		(appointmentRequestId,
		hideParticipantsMode,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId);
        
        select formatCSL(timeSlots) into formattedTimeSlots; 
 		SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        set timeSlotPreference=1;
 		WHILE formattedTimeSlots != '' > 0 DO
        SET @timeslot = SUBSTRING_INDEX(formattedTimeSlots, @separator, 1);
 			INSERT INTO `appointment_request_timeslot`
				(`appointment_request_id`,
				`timeslot`,
				`timeslot_preference`,
				`appointment_request_status_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(appointmentRequestId,
				@timeslot,
				timeSlotPreference,
				@appointmentRequestStatusId,
				createdBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
			set timeSlotPreference= timeSlotPreference+1;
			SET formattedTimeSlots = SUBSTRING(formattedTimeSlots, CHAR_LENGTH(@timeslot) + @separatorLength + 1);
		END WHILE;  
        
        select user_name, email_address into @userName, @emailAddress 
			from pokket_user where pokket_user_id = toPokketUserId;
            
		IF @userName IS NOT NULL THEN       
        
        
			IF fromPokketUserId != createdBy THEN  
				select concat(first_name,' ',last_name) into @creatorFullName
					from pokket_user where pokket_user_id = createdBy;
				select concat(@creatorFullName, ' (on behalf of ', first_name,' ',last_name, ')') into @pokketUserFullName
					from pokket_user where pokket_user_id = fromPokketUserId;
            ELSE
				select concat(first_name,' ',last_name) into @pokketUserFullName
					from pokket_user where pokket_user_id=
					if(isWithAssignedCm=1,fromPokketUserId,createdBy);
            END IF;
					
		   select REPLACE(notification_text_template,'<POKKET_USER_NAME>',@pokketUserFullName)
				into @notificationTextTemplate from ref_notification_type where notification_type_id=7;     
		
			INSERT INTO `notification`
				(`notification_type_id`,
				`notification_batch_id`,
				 `pokket_user_id`,
				 `notification_from`,
				 `organization_service_id`,
				 `email_address`,
				`notification_text`,
				`notification_dtm`,
				`action_type_id`,
				`action_ref_id`,
				`is_notified`,
				`is_read`,
				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				values (7,notificationBatchId,
				toPokketUserId,
				 if(isWithAssignedCm=1,fromPokketUserId,createdBy),
				serviceId,
				@emailAddress,
				@notificationTextTemplate,
				CURRENT_TIMESTAMP,5,appointmentRequestId,
				0,0,createdBy,CURRENT_TIMESTAMP,
				awsRequestId
			);
     END IF;

   COMMIT;
	
    	SELECT `appointment_request`.`appointment_request_id` as appointmentRequestId,
			`appointment_request`.`from_pokket_user_id` as fromPokketUserId,
            A.first_name as  fromPokketUserFirstName,
            A.last_name as fromPokketUserLastName,
            B.first_name as  toPokketUserFirstName,
            B.last_name as toPokketUserLastName,
            CONCAT(F.first_name,' ',F.last_name) as createdBy,
			`appointment_request`.`to_pokket_user_id` as toPokketUserId,
			`appointment_request`.`meeting_purpose` as meetingPurpose,
            `appointment_request`.`location` as location,
            `appointment_request`.`video_meeting_id` as videoMeetingId,
            `appointment_request`.`request_note` as requestNote,
            `appointment_request`.`accept_note` as acceptNote,
            `appointment_request`.`reject_note` as rejectNote,
			`appointment_request`.`appointment_request_status_id` as appointmentRequestStatus,
             `appointment_request`.`is_with_assigned_cm` as isWithAssignedCm,
             if(createdBy = fromPokketUserId,1,0) as isWithAssignedCm,
             `appointment_request`.`duration_in_minutes` as duration,
            timeslot_id as timeSlotId,
            timeslot_preference as timeSlotPreference,
            timeslot as timeSlot,
            appointment_request.service_id as serviceId,
            organization_service.service_name as serviceName,
            `appointment_request`.`appointment_batch_id` as appointmentBatchId,
            appointment_settings.hide_participants_mode as hideParticipantsMode
			FROM `appointment_request` 
            INNER JOIN pokket_user A on
            `appointment_request`.`from_pokket_user_id`=A.pokket_user_id
            LEFT JOIN pokket_user B on
            `appointment_request`.`to_pokket_user_id`=B.pokket_user_id
            INNER JOIN pokket_user F on
            appointment_request.created_by=F.pokket_user_id
            inner join appointment_request_timeslot
            on `appointment_request`.`appointment_request_id`=`appointment_request_timeslot`.`appointment_request_id`
            left join organization_service on
            appointment_request.service_id=organization_service.organization_service_id
            left join appointment_settings on
            appointment_request.appointment_request_id = appointment_settings.appointment_request_id
            where `appointment_request`.`appointment_request_id`=appointmentRequestId;	  
    CALL uspGetPokketUser(fromPokketUserId,createdBy,awsRequestId);
    CALL uspGetOutgoingAppointmentRequests(fromPokketUserId,0,null,awsRequestId, "!PAST");

END ;;
