CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspInsertMessage`(
		messageThreadId varchar(50),
        messageThreadTitle varchar(250),
        serviceId varchar(50),
        messageId varchar(50),
        messageText varchar(1000),
        replyToMessageId varchar(50),
        fromPokketUserId varchar(50),
        toPokketUserIds varchar(250),
        createdBy varchar(50),
        awsRequestId varchar(50))
BEGIN
    DECLARE formattedToPokketUserIds varchar(250);
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'An account with the phone number/ssn already exists';
				ELSE
					set displayError = 'An account with the phone number/ssn already exists';
				END IF;
			END IF;
            SET displayError=@full_error;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
		SET @caretseparator = '^';
		SET @caretSeparatorLength = CHAR_LENGTH(@caretseparator);
        
        
		SET @fromUserId = SUBSTRING_INDEX(fromPokketUserId, '^', 1);
		SET @fromServiceId= SUBSTRING_INDEX(fromPokketUserId, '^', -1);


    
    select count(*) into @countOfMessageThreadId 
    from message_thread
    where message_thread_id=messageThreadId;
    
    if @countOfMessageThreadId = 0 then
	   INSERT INTO `message_thread`
		(`message_thread_id`,
		`message_thread_title`,
		`is_active`,
		`aws_request_id`,
		`created_by`,
		`created_dtm`)
		VALUES
		(messageThreadId,
		messageThreadTitle,
		1,
		awsRequestId,
		createdBy,
		CURRENT_TIMESTAMP);
        
        INSERT INTO `pokket_user_message_thread`
			(`message_thread_id`,
			`pokket_user_id`,
            `service_id`.
            `is_active`,
			`aws_request_id`,
			`created_by`,
			`created_dtm`)
			VALUES
			(messageThreadId,
			@fromUserId,
            @fromServiceId,
            1,
			awsRequestId,
			createdBy,
			CURRENT_TIMESTAMP);
    else
		select count(*) into @countOfFromPokketUserId 
        from pokket_user_message_thread where 
        pokket_user_id=@fromUserId and
        service_id=@fromServiceId and 
        message_thread_id=messageThreadId;
        if @countOfFromPokketUserId=0 then
			INSERT INTO `pokket_user_message_thread`
			(`message_thread_id`,
			`pokket_user_id`,
            `service_id`,
            `is_active`,
			`aws_request_id`,
			`created_by`,
			`created_dtm`)
			VALUES
			(messageThreadId,
			@fromUserId,
            @fromServiceId,
            1,
			awsRequestId,
			createdBy,
			CURRENT_TIMESTAMP);
        else
			UPDATE `pokket_user_message_thread`
            set is_active=1,is_archived=0, aws_request_id=awsRequestId,
            modified_by=createdBy,
            modified_dtm=CURRENT_TIMESTAMP
            where pokket_user_id=@fromUserId and
            service_id=@fromServiceId and 
			message_thread_id=messageThreadId;
        
        end if;
    end if;
    
    
    
    select is_locked into @isLocked from pokket_user_message_thread where
    pokket_user_id = @fromUserId and service_id = @fromServiceId 
    and message_thread_id=messageThreadId;
    
    if @isLocked <> 1 then
    
    INSERT INTO `message`
	(`message_id`,
	`message_thread_id`,
	`message_text`,
	`reply_to_message_id`,
	`aws_request_id`,
	`created_by`,
	`created_dtm`)
	VALUES
	(messageId,
	messageThreadId,
	messageText,
	replyToMessageId,
	awsRequestId,
	createdBy,
	CURRENT_TIMESTAMP);
    
    INSERT INTO `pokket_user_message`
			(`message_id`,
			`pokket_user_id`,
			`message_direction`,
            `is_read`,
			`aws_request_id`,
			`created_by`,
			`created_dtm`)
			VALUES
			(messageId,
			createdBy,
			'FROM',
            1,
			awsRequestId,
			createdBy,
			CURRENT_TIMESTAMP);

                
		
		
		select formatCSL(toPokketUserIds) 
			into formattedToPokketUserIds;
		WHILE formattedToPokketUserIds != '' > 0 DO
			SET @toPokketUserIdWithServiceId = SUBSTRING_INDEX(formattedToPokketUserIds, '|', 1);
            SET @toPokketUserId=SUBSTRING_INDEX(@toPokketUserIdWithServiceId, @caretseparator, 1);
            SET @toServiceId=SUBSTRING_INDEX(@toPokketUserIdWithServiceId, @caretseparator, -1);
               
                select is_locked into @isLocked from pokket_user_message_thread where
				pokket_user_id = @toPokketUserId and service_id = @toServiceId
                and message_thread_id=messageThreadId;
                if @isLocked <> 1 then
						INSERT INTO `pokket_user_message`
						(`message_id`,
						`pokket_user_id`,
						`message_direction`,
						`aws_request_id`,
						`created_by`,
						`created_dtm`)
						VALUES
						(messageId,
						@toPokketUserId,
						'TO',
						awsRequestId,
						createdBy,
						CURRENT_TIMESTAMP);
						
						select count(*) into @countOfToPokketUserId 
						from pokket_user_message_thread where 
						pokket_user_id=@toPokketUserId and
						service_id=@toServiceId and 
						message_thread_id=messageThreadId;
						if @countOfToPokketUserId=0 then
							INSERT INTO `pokket_user_message_thread`
							(`message_thread_id`,
							`pokket_user_id`,
							`service_id`,
							`is_active`,
							`aws_request_id`,
							`created_by`,
							`created_dtm`)
							VALUES
							(messageThreadId,
							@toPokketUserId,
							@toServiceId,
							1,
							awsRequestId,
							createdBy,
							CURRENT_TIMESTAMP);
						else
							UPDATE `pokket_user_message_thread`
							set is_active=1,is_archived=0,aws_request_id=awsRequestId,
							modified_by=createdBy,
							modified_dtm=CURRENT_TIMESTAMP
							where pokket_user_id=@toPokketUserId and
							service_id=@toServiceId and 
							message_thread_id=messageThreadId;
					
						end if;
                  end if;      
                
		  SET formattedToPokketUserIds = SUBSTRING(formattedToPokketUserIds, CHAR_LENGTH(@toPokketUserId) + @caretSeparatorLength + CHAR_LENGTH(@toServiceId) + 2);
		END WHILE;	
    end if;
   
   COMMIT;
   
   
	CALL uspGetMessage(messageId,awsRequestId);
    CALL uspGetMessageThread(messageThreadId,@fromUserId,awsRequestId);
    CALL uspGetMessages(messageThreadId,@fromUserId,awsRequestId);
    CALL uspGetMessageThreads(@fromUserId,@fromUserId,awsRequestId,0);
    CALL uspGetPokketUser(@fromUserId,createdBy,awsRequestId);

END ;;
