CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspInsertOrganizationService`(
		organizationServiceId varchar(50),
        organizationId varchar(50),
        serviceName varchar(100),
        needCategoryId tinyint(4),
        minimumAge tinyint(4),
        maximumAge tinyint(4),
        isReferralRequired tinyint(1),
        gender tinyint(4),
        isAvailableForSexOffender tinyint(1),
        isAvailableForFamilyChildren tinyint(1),
        releaseOfInformationText mediumtext,
        payscales varchar(250),
        prescreenQuestions mediumtext,
        serviceIntakeQuestions mediumtext,
        facilityIds varchar(2500),
        referralDocRequirement tinyint(4),
        referralDocDesc mediumtext,
        createdBy varchar(50),
        awsRequestId varchar(50),
        serviceType VARCHAR(35),
        serviceArea VARCHAR(35),
        serviceStates varchar(200),
        isSearchable tinyint(1),
        serviceDescription varchar(500),
        serviceContactInfo varchar(100),
        veteranOnly tinyint(1)
        )
BEGIN
    DECLARE formattedFacilityIds varchar(2500);
    DECLARE formattedPayscales varchar(250);
    DECLARE formattedPrescreenQuestions mediumtext;
    DECLARE formattedServiceIntakeQuestions mediumtext;
    DECLARE formattedServiceStates varchar(200);
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
       
	INSERT INTO `organization_service`
		(`organization_service_id`,
		`organization_id`,
		`service_name`,
		`service_status_id`,
		`need_category_id`,
		`service_eligibility_min_age`,
		`service_eligibility_max_age`,
		`is_referral_required`,
		`service_eligibility_gender_id`,
		`is_available_for_sex_offender`,
		`is_available_for_family_children`,
		`release_of_information_text`,
		`created_by`,
		`created_dtm`,`aws_request_id`,
        `referral_doc_requirement`,
        `referral_doc_desc`,
        `service_type`,
		`service_area`,
        `is_searchable`,
        `service_description`,
        `service_contact_info`,
        `veteran_only`
	    )
		VALUES
		(organizationServiceId,
		organizationId,
		serviceName,
		2, 
		needCategoryId,
		minimumAge,
		maximumAge,
		isReferralRequired,
		gender,
		isAvailableForSexOffender,
		isAvailableForFamilyChildren,
		releaseOfInformationText,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId,
        referralDocRequirement,
        referralDocDesc,
        serviceType,
        serviceArea,
        isSearchable,
        serviceDescription,
        serviceContactInfo,
        veteranOnly
		);
	
	IF serviceType = 'VIRTUAL' THEN
		
        select organization_facility_id into @virtualFacilityId
        FROM organization_facility
        WHERE is_virtual = 1
        AND organization_id = organizationId;
        
        IF @virtualFacilityId is null THEN
			SET @newVirtualFacilityId = uuid();
			INSERT INTO `organization_facility`
				(`organization_facility_id`,
				`organization_id`,
				`facility_name`,
				`facility_status_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`,
				`is_virtual`
				)
				VALUES
				(@newVirtualFacilityId,
				organizationId,
				'VIRTUAL',
				2,
				createdBy,
				CURRENT_TIMESTAMP,
				awsRequestId,
				1
				);
			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(@newVirtualFacilityId,
				organizationServiceId,
				createdBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
        ELSE
			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(@virtualFacilityId,
				organizationServiceId,
				createdBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
        END IF;
        
	END IF;

        SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        
        
        select formatCSL(facilityIds) into formattedFacilityIds; 
 		WHILE formattedFacilityIds != '' > 0 DO
			SET @facilityId = SUBSTRING_INDEX(formattedFacilityIds, @separator, 1);
 			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(@facilityId,
				organizationServiceId,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);
			SET formattedFacilityIds = SUBSTRING(formattedFacilityIds, CHAR_LENGTH(@facilityId) + @separatorLength + 1);
		END WHILE;   
        
        select formatCSL(payscales) into formattedPayscales; 
 		WHILE formattedPayscales != '' > 0 DO
			SET @payScaleId = SUBSTRING_INDEX(formattedPayscales, @separator, 1);
 
			INSERT INTO `organization_service_payscale`
				(`organization_service_id`,
				`pay_scale_id`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@payScaleId,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
		SET formattedPayscales = SUBSTRING(formattedPayscales, CHAR_LENGTH(@payScaleId) + @separatorLength + 1);
		END WHILE;
        
        SET @caretseparator = '^';
		SET @caretSeparatorLength = CHAR_LENGTH(@caretseparator);
        
         select prescreenQuestions into formattedPrescreenQuestions; 
        WHILE formattedPrescreenQuestions != '' > 0 DO
			SET @prescreenQuestionWithAnswer = SUBSTRING_INDEX(formattedPrescreenQuestions, @caretseparator, 1);
			SET @prescreenQuestion = SUBSTRING_INDEX(@prescreenQuestionWithAnswer, '|', 1);
            SET @prescreenQuestionQualifyingAnswer= SUBSTRING_INDEX(@prescreenQuestionWithAnswer, '|', -1);
			INSERT INTO `prescreen_question`
				(`organization_service_id`,
				`prescreen_question_description`,
                `prescreen_question_qualifying_answer`,
                `is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@prescreenQuestion,
                @prescreenQuestionQualifyingAnswer,
                1,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
			SET formattedPrescreenQuestions = SUBSTRING(formattedPrescreenQuestions, CHAR_LENGTH(@prescreenQuestionWithAnswer) + @caretSeparatorLength + 1);
		END WHILE;
        
        
        select serviceIntakeQuestions into formattedServiceIntakeQuestions; 
        WHILE formattedServiceIntakeQuestions != '' > 0 DO
			SET @serviceIntakeQuestion = SUBSTRING_INDEX(formattedServiceIntakeQuestions, @caretseparator, 1);
			
			INSERT INTO `service_intake_question`
				(`organization_service_id`,
				`service_intake_question_description`,
                `is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@serviceIntakeQuestion,
                1,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
			SET formattedServiceIntakeQuestions = SUBSTRING(formattedServiceIntakeQuestions, CHAR_LENGTH(@serviceIntakeQuestion) + @caretSeparatorLength + 1);
		END WHILE;
        
	IF releaseOfInformationText is not null AND releaseOfInformationText != ''
		
		THEN
			INSERT INTO `agreement_user`
				(`agreement_id`,
				`version`,
                `is_latest_version`,
                `agreement_type`,
                `agreement_name`,
                `release_date`,
                `requiring_org_id`,
                `service_id`,
                `agreement_text`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(uuid(),
                1,
                1,
                'ROI',
                'service_roi',
                CURRENT_TIMESTAMP,
                organizationId,
                organizationServiceId,
                releaseOfInformationText,
				createdBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
	END IF;
    
	IF serviceArea = 'STATEWIDE' AND serviceStates != ''
	
	THEN
		select formatCSL(serviceStates) into formattedServiceStates; 
 		WHILE formattedServiceStates != '' > 0 DO
			SET @stateCd = SUBSTRING_INDEX(formattedServiceStates, @separator, 1);
 			INSERT INTO `service_state`
			(`service_id`,
			`state_cd`,
			`is_active`,
			`created_by`,
			`aws_request_id`)
			VALUES
			(organizationServiceId,
			@stateCd,
			1,
			createdBy,
			awsRequestId);
			SET formattedServiceStates = SUBSTRING(formattedServiceStates, CHAR_LENGTH(@stateCd) + @separatorLength + 1);
		END WHILE;
	END IF;
        
        
     COMMIT;  
     
     
     
	CALL uspGetService(organizationServiceId,awsRequestId);
    CALL uspGetOrganization(createdBy,null,awsRequestId);
    CALL uspGetOrganizationServices(organizationId,awsRequestId);

END ;;
