BEGIN
    DECLARE v_finished INTEGER DEFAULT FALSE;
	DECLARE v_sharedWithPokketUserId varchar(50) ;
    DECLARE v_shareDurationId tinyint(4) ;
    DECLARE v_shareDurationUnits tinyint(4);
 
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    
    
    
    DECLARE already_shared_with_users_by_need_category_cursor CURSOR FOR 
	SELECT distinct shared_with,share_duration_id,share_duration_units 
     FROM `pokket_user_share`
	 WHERE 
	`pokket_user_share`.`shared_by`=pokketUserId AND
    `pokket_user_share`.`share_type_id`=2 and 
    `pokket_user_share`.`need_category_id`=needCategoryId and 
	`pokket_user_share`.`is_active`=1 
	and CURRENT_TIMESTAMP <=  CASE WHEN `pokket_user_share`.`share_duration_id`=1 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units HOUR) 
	WHEN `pokket_user_share`.`share_duration_id`=2 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units DAY) 
	WHEN `pokket_user_share`.`share_duration_id`=3 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units WEEK) 
	WHEN `pokket_user_share`.`share_duration_id`=4 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units MONTH)
	ELSE date_ADD(share_start_dtm, INTERVAL share_duration_units YEAR) END;
    
    
	
    
    DECLARE already_shared_with_users_by_entire_plan_cursor CURSOR FOR 
	SELECT distinct shared_with,share_duration_id,share_duration_units 
     FROM `pokket_user_share`
	 WHERE 
	`pokket_user_share`.`shared_by`=pokketUserId AND
    `pokket_user_share`.`share_type_id`=1 and 
	`pokket_user_share`.`is_active`=1
	and CURRENT_TIMESTAMP <=  CASE WHEN `pokket_user_share`.`share_duration_id`=1 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units HOUR) 
	WHEN `pokket_user_share`.`share_duration_id`=2 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units DAY) 
	WHEN `pokket_user_share`.`share_duration_id`=3 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units WEEK) 
	WHEN `pokket_user_share`.`share_duration_id`=4 THEN
	date_ADD(share_start_dtm, INTERVAL share_duration_units MONTH)
	ELSE date_ADD(share_start_dtm, INTERVAL share_duration_units YEAR) END;
    
     
	DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET v_finished = TRUE;
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    
    
    
    
    
    select count(*) into @cmRoleCount from pokket_user_role
    where pokket_user_id=createdBy and user_role_id=4;
