		VALUES
		(participantPlanItemId,
		description,
		pokketUserId,
		parentPlanItemId,
		planItemTypeId,
		location,
        startTime,
		dueDate,
		statusId,
        notes,
		needCategoryId,
		ifnull(repeatInterval,0),
		ifnull(repeatTypeId,'NONE'),
		servicePlanItemId,
		severityId,
        priority,
        actionTypeId,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId,
        serviceId,
        appointmentRequestId,
        duration);
      else  
      
		INSERT INTO `plan_item_recurrence_pattern`
		(`recurrence_pattern_id`,
		`recurrence_pattern_rule`,
		`created_by`,
		`created_dtm`,
		`aws_request_id`)
		VALUES
		(recurrencePatternId,
		recurrencePatternRule,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId);
        
        
        INSERT INTO `participant_plan_item`
		(`participant_plan_item_id`,
		`participant_plan_item_description`,
		`pokket_user_id`,
		`parent_plan_item_id`,
		`plan_item_type_id`,
		`location`,
        `start_time`,
		`due_date`,
		`status_id`,
        `notes`,
		`need_category_id`,
		`repeat_interval`,
		`repeat_type_id`,
        `recurrence_pattern_id`,
		`service_plan_item_id`,
		`severity_id`,
        `priority`,
        `action_type_id`,
		`created_by`,
		`created_dtm`,
        `aws_request_id`,
         `service_id`,
         `appointment_request_id`,
         `duration_in_minutes`)
		VALUES
		(participantPlanItemId,
		description,
		pokketUserId,
		parentPlanItemId,
		planItemTypeId,
		location,
        startTime,
		dueDate,
		statusId,
        notes,
		needCategoryId,
		ifnull(repeatInterval,0),
		ifnull(repeatTypeId,'NONE'),
        recurrencePatternId,
