        priority,
        actionTypeId,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId,
        serviceId,
        appointmentRequestId,
        duration);
        
        INSERT INTO `plan_item_recurrence`
		(`recurrence_id`,
		`plan_item_id`,
		`recurrence_pattern_id`,
		`start_time`,
		`due_date`,
		`location`,
		`status_id`,
		`notes`,
        `is_original_plan_item`,
		`created_by`,
		`created_dtm`,
		`aws_request_id`)
		VALUES
		(recurrenceId,
		participantPlanItemId,
		recurrencePatternId,
		startTime,
		dueDate,
		location,
		statusId,
		notes,
        1,
		createdBy,
		CURRENT_TIMESTAMP,
		awsRequestId); 

        
   end if;

	
 		

    
    
    set @notificationBatchId = uuid();
    
     
    
    
     if @cmRoleCount > 0 then
 		call mypokket.uspInsertPokketUserShare(UUID(), pokketUserId, serviceId, participantPlanItemId, 3, 5, 1, needCategoryId,@notificationBatchId, createdBy,awsRequestId);
    end if; 

	
    
    OPEN already_shared_with_users_by_need_category_cursor;
    get_nc_share: LOOP
		FETCH already_shared_with_users_by_need_category_cursor INTO 
		v_sharedWithPokketUserId,v_shareDurationId,v_shareDurationUnits;
 
		IF v_finished  THEN 
			LEAVE get_nc_share;
		END IF;
 
		call mypokket.uspInsertPokketUserShare(UUID(), pokketUserId, v_sharedWithPokketUserId, participantPlanItemId, 2, v_shareDurationId, v_shareDurationUnits, needCategoryId,@notificationBatchId, createdBy,awsRequestId);
 
	  END LOOP get_nc_share;
 
	CLOSE already_shared_with_users_by_need_category_cursor;
    
    SET v_finished = FALSE;
    
	
    
    OPEN already_shared_with_users_by_entire_plan_cursor;
    get_entire_plan_share: LOOP
		FETCH already_shared_with_users_by_entire_plan_cursor INTO 
		v_sharedWithPokketUserId,v_shareDurationId,v_shareDurationUnits;

		IF v_finished  THEN 
			LEAVE get_entire_plan_share;
