		call mypokket.uspInsertPokketUserShare(UUID(), pokketUserId, v_sharedWithPokketUserId, participantPlanItemId, 1, v_shareDurationId, v_shareDurationUnits, needCategoryId,@notificationBatchId, createdBy,awsRequestId);
 
	  END LOOP get_entire_plan_share;
 
	CLOSE already_shared_with_users_by_entire_plan_cursor;

    
	
   COMMIT;
   

  

END ;;
