				`created_dtm`,`aws_request_id`)
				VALUES
				(eventId,
				@inviteeId,
				eventTitle,
				eventDescription,
				eventLocation,
				eventTimezone,
				eventStartDateTime,
				eventEndDateTime,
				TIMESTAMPDIFF(MINUTE,eventStartDateTime,eventEndDateTime),
				repeatInterval,
				repeatTypeId,
				1,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);
            
				INSERT INTO `pokket_user_event_invitee`
