    if shareTypeId=1  then 
    
    
			select count(*) into @countOfExistingShares from pokket_user_share
				where shared_by= sharedByPokketUserId and 
				shared_with=sharedWithServiceId and share_type_id=shareTypeId;
    
			if @countOfExistingShares>0 then
				
