				 		participant_plan_item_id in (select participant_plan_item_id from participant_plan_item 
                        where pokket_user_id=sharedByPokketUserId));
 	end if;       
       
	if shareTypeId=2  then 
    
			select count(*) into @countOfExistingShares from pokket_user_share
				where shared_by= sharedByPokketUserId and 
				shared_with=sharedWithServiceId and share_type_id=shareTypeId
                and need_category_id in 
                (select need_category_id FROM ref_need_category
					WHERE FIND_IN_SET(`ref_need_category`.`need_category_id`, needCategoryIds));

			if @countOfExistingShares > 0 then
                   
						UPDATE `pokket_user_share` set is_active=1,
