								share_duration_units=shareDurationUnits,
								is_accepted=1,
								modified_by=createdBy,
								modified_dtm=CURRENT_TIMESTAMP,
								`aws_request_id`=awsRequestId
								where `share_type_id` =4       
								and `shared_by`= sharedByPokketUserId
                                and shared_with=sharedWithServiceId
                                and shared_ref_id in (select document_id FROM `pokket_user_document`
									WHERE  `pokket_user_document`.`pokket_user_id`=sharedByPokketUserId
									and FIND_IN_SET(`pokket_user_document`.`need_category_id`, needCategoryIds));
                                                            
				else
					
					INSERT INTO `pokket_user_share`
						(`share_id`,
						`shared_by`,
						`shared_with`,
						`shared_ref_id`,
						`share_start_dtm`,
						`share_type_id`,
						`share_duration_id`,
						`share_duration_units`,
						`need_category_id`,
						`is_active`,
						`is_accepted`,
