CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspIsDocumentShared`(
		documentUrl varchar(250),
        viewerPokketUserId varchar(50),awsRequestId varchar(50))
BEGIN

select count(*) into @ownershipCount
from (

select distinct document.document_id
from document
where document.document_url = documentUrl
and document.created_by = viewerPokketUserId

UNION
select distinct document.document_id
from document
inner join plan_item_document on document.document_id = plan_item_document.document_id
inner join participant_plan_item on plan_item_document.participant_plan_item_id = participant_plan_item.participant_plan_item_id
inner join pokket_user on participant_plan_item.created_by = pokket_user.pokket_user_id
inner join facility_service_case_manager on participant_plan_item.service_id = facility_service_case_manager.service_id
where document.document_url = documentUrl
and facility_service_case_manager.pokket_user_id = viewerPokketUserId 
and facility_service_case_manager.is_active = 1
and not document.created_by = participant_plan_item.pokket_user_id

UNION
select distinct document.document_id
from document
inner join pokket_user_document on document.document_id = pokket_user_document.document_id
inner join pokket_user on pokket_user_document.created_by = pokket_user.pokket_user_id
inner join facility_service_case_manager on document.service_id = facility_service_case_manager.service_id
where document.document_url = documentUrl
and facility_service_case_manager.pokket_user_id = viewerPokketUserId 
and facility_service_case_manager.is_active = 1
and not document.created_by = pokket_user_document.pokket_user_id
) O;

if @ownershipCount > 0 then
select 1;
else 
 
 
		select count(*) into @referralDocumentCount
from participant_referral join participant_referral_document on 
participant_referral.participant_referral_id=participant_referral_document.referral_id 
inner join document on participant_referral_document.document_id=document.document_id
join organization_service on participant_referral.service_id=organization_service.organization_service_id
join pokket_user_organization on organization_service.organization_id = pokket_user_organization.organization_id
where pokket_user_organization.pokket_user_id = viewerPokketUserId
and document_url=documentUrl;

if @referralDocumentCount > 0 then
select 1;
else 

		set @planItemId = null;
        
        		select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=viewerPokketUserId 
		group by pokket_user_id) AS A;

		
        
        select participant_plan_item.pokket_user_id,participant_plan_item.participant_plan_item_id,
document.document_id, participant_plan_item.need_category_id, parent_plan_item_id
 into @pokketUserId, @planItemId, @documentId,@planItemNeedCategoryId, @parentPlanItem
 from document inner join plan_item_document
 on document.document_id=plan_item_document.document_id 
 inner join participant_plan_item on 
 plan_item_document.participant_plan_item_id = participant_plan_item.participant_plan_item_id
 where document_url=documentUrl;
 
 select parent_plan_item_id into @parentParentPlanItem from participant_plan_item
 where participant_plan_item_id = @parentPlanItem;


if @planItemId is null then

			select pokket_user_id, document.document_id, doc_category_ref_id,doc_category_id into @pokketUserId, @documentId, 
            @participantReferralId,@docCategoryId
		from document inner join pokket_user_document
		on document.document_id=pokket_user_document.document_id 
		where document_url=documentUrl;
        
					
                    if viewerPokketUserId = @pokketUserId then
						select 1;
					else
						
						select count(*) into @countOfDirectShares from vw_active_pokket_user_shares 
						where share_type_id=4 and shared_ref_id=@documentId and 
						shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                        
                        select count(*) into @countOfSharedViaEntireFileCabinetShare from vw_active_pokket_user_shares 
							where share_type_id=5 and shared_by=@pokketUserId and FIND_IN_SET(shared_with,@serviceIds)
                            AND @documentId NOT IN ( 
								SELECT shared_ref_id from pokket_user_share where shared_by=@pokketUserId AND
								share_type_id=4 AND is_active=0 and FIND_IN_SET(shared_with,@serviceIds));
                        
                        select organization_id into @orgId from pokket_user_organization
                        where pokket_user_id=viewerPokketUserId;
                        select count(*) into @countOfSpaRole from pokket_user_role
                        where pokket_user_id = viewerPokketUserId and user_role_id=3;
                        select count(*) into @countOfReferrals from participant_referral 
                        where participant_referral_id = @participantReferralId  and 
                        service_id in (select organization_service_id from organization_service
                        where organization_id = @orgId)
                        and @docCategoryId = 2
                        and @countOfSpaRole> 0;
                        set @canViewPlanItem= (@countOfDirectShares + @countOfReferrals + @countOfSharedViaEntireFileCabinetShare) > 0;
                        select if(@canViewPlanItem,1,0) as canViewDocument;
                    end if;

else

					select count(*) into @countOfDirectShares from vw_active_pokket_user_shares 
					where share_type_id=3 and shared_ref_id=@planItemId and 
                    shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                    
					select count(*) into @countOfDirectUnShares from pokket_user_share
					where share_type_id=3 and shared_ref_id=@planItemId and 
                    shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds)
                    and is_active=0;
                    
 					select count(*) into @countOfWholePlanShares from vw_active_pokket_user_shares 
						where share_type_id=1 and shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                    
 					select count(*) into @countOfNeedCategoryShares from vw_active_pokket_user_shares 
						where share_type_id=2 and need_category_id=@planItemNeedCategoryId and  
                         shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
					
					select count(*) into @countOfDirectParentShares from vw_active_pokket_user_shares 
					where share_type_id=3 and shared_ref_id=@parentPlanItem and 
                    shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                    
					select count(*) into @countOfDirectGrandparentShares from vw_active_pokket_user_shares 
					where share_type_id=3 and shared_ref_id=@parentParentPlanItem and 
                    shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                    
					select count(*) into @countOfDirectDocumentShares from vw_active_pokket_user_shares 
					where share_type_id=4 and shared_ref_id=@documentId and 
					shared_by=@pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
                    
                    select count(*) into @countOfSharedViaEntireFileCabinetShare from vw_active_pokket_user_shares 
							where share_type_id=5 and shared_by=@pokketUserId and FIND_IN_SET(shared_with,@serviceIds)
                            AND @documentId NOT IN ( 
								SELECT shared_ref_id from pokket_user_share where shared_by=@pokketUserId AND
								share_type_id=4 AND is_active=0 and FIND_IN_SET(shared_with,@serviceIds));
					
                    if viewerPokketUserId = @pokketUserId then
						set @canViewPlanItem=true;
                    elseif @countOfDirectUnShares > 0 then
						set @canViewPlanItem=false;
                    else
						set @canViewPlanItem= (@countOfDirectShares + @countOfWholePlanShares + 
							@countOfNeedCategoryShares + @countOfDirectParentShares + @countOfDirectGrandparentShares + 
							@countOfDirectDocumentShares + @countOfSharedViaEntireFileCabinetShare) > 0;
					end if;		
                    
                    select if(@canViewPlanItem,1,0) as canViewDocument;
	end if ;

end if;

end if;

END ;;
