CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspMarkNotificationsAsRead`(
        pokketUserId varchar(50),
        notificationIds varchar(5000),
        markAllAsRead tinyint(1),
        modifiedBy varchar(50),
        awsRequestId varchar(50))
BEGIN
    
    DECLARE formattedNotificationIds varchar(5000);
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'Record already exists.';
                end if;    
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
      if markAllAsRead=1 then
      
		UPDATE notification
            SET is_read=1,
            modified_by=modifiedBy,
            modified_dtm=CURRENT_TIMESTAMP,
            aws_request_id=awsRequestId
            where pokket_user_id=pokketUserId;
      else
     
		SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        select formatCSL(notificationIds) into formattedNotificationIds; 
 		WHILE formattedNotificationIds != '' > 0 DO
			SET @notificationId = SUBSTRING_INDEX(formattedNotificationIds, @separator, 1);
 			UPDATE notification
            SET is_read=1,
            modified_by=modifiedBy,
            modified_dtm=CURRENT_TIMESTAMP,
            aws_request_id=awsRequestId
            where notification_id=@notificationId;
			SET formattedNotificationIds = SUBSTRING(formattedNotificationIds, CHAR_LENGTH(@notificationId) + @separatorLength + 1);
		END WHILE;   
     end if;   
        
     COMMIT;   
   
   CALL uspGetPokketUser(pokketUserId,modifiedBy,awsRequestId);
    

END ;;
