CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspMarkPlanItemAsComplete`(
		planItemId varchar(50),
        recurrenceId varchar(50),
        completionDate varchar(30),
        wasNeedMet tinyint(4),
        completionNotes varchar(2500),
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    select repeat_type_id,is_mandatory,created_by, participant_plan_item_description,pokket_user_id, 
    service_id,appointment_request_id 
    into @repeatTypeId, @isMandatory, @createdBy,@planItemDesc, @participantPokketUserId,@serviceId,@appointmentRequestId  from
    participant_plan_item 
    where participant_plan_item_id=planItemId;
    
    
    IF @appointmentRequestId is not null THEN
		UPDATE `appointment_request`
			SET
			`appointment_request_status_id` = 7,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId 
			WHERE `appointment_request_id` = @appointmentRequestId;    
    END IF;
    
    if @repeatTypeId='NONE' then
		UPDATE `participant_plan_item`
			SET
			`completed_by` = modifiedBy,
            `completion_date` = completionDate,
			`status_id` = 3,
			`is_need_met_after_completion` = wasNeedMet,
            `completion_notes`=completionNotes,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId 
			WHERE `participant_plan_item_id` = planItemId;
            
            select due_date , pokket_user_id into @dueDate,@pokketUserId from participant_plan_item where participant_plan_item_id= planItemId;
            if (str_to_date(@dueDate,'%m/%d/%Y %h:%i %p') < current_timestamp ) then
				update participant set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId;
                
                update participant_task_counts set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId
                and shared_with_pokket_user_id in (select pokket_user_id from 
                facility_service_case_manager where service_id in (
                select shared_with from pokket_user_share where shared_ref_id=planItemId
                ));
                
            end if;
    else    
        
        
        
        select count(*) into @countOfExistingRecurrence from 
        `plan_item_recurrence` where `plan_item_id`=planItemId
        and `recurrence_id`=recurrenceId;
        
        if @countOfExistingRecurrence !=0 then
			UPDATE `plan_item_recurrence`
			SET
			`completed_by` = modifiedBy,
            `completion_date` = completionDate,
			`status_id` = 3,
			`is_need_met_after_completion` = wasNeedMet,
			`completion_notes`=completionNotes,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId 
			WHERE `plan_item_id` = planItemId 
			and `recurrence_id`=recurrenceId;    
			 
            select due_date  into @dueDate from plan_item_recurrence where plan_item_id= planItemId and recurrence_id=recurrenceId;
            select  pokket_user_id into @pokketUserId from participant_plan_item where participant_plan_item_id= planItemId;
            if (str_to_date(@dueDate,'%m/%d/%Y %h:%i %p') < current_timestamp ) then
				update participant set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId;
                
				update participant_task_counts set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId
                and shared_with_pokket_user_id in (select pokket_user_id from 
                facility_service_case_manager where service_id in (
                select shared_with from pokket_user_share where shared_ref_id=planItemId
                ));

            end if;

          else
          INSERT INTO `mypokket`.`plan_item_recurrence`
			(`recurrence_id`,
			`plan_item_id`,
			`recurrence_pattern_id`,
			`start_time`,
			`due_date`,
			`location`,
			`completion_date`,
            `completed_by`,
			`status_id`,
			`is_need_met_after_completion`,
            `completion_notes`,
			`created_by`,
			`created_dtm`,
			`aws_request_id`)
            SELECT recurrenceId, 
				 planItemId,
				`participant_plan_item`.`recurrence_pattern_id`,
                `participant_plan_item`.`start_time`,
                `participant_plan_item`.`due_date`,
				`participant_plan_item`.`location`,
				 completionDate,
                 modifiedBy,
				 3,
 				wasNeedMet,
                completionNotes,
				modifiedBy,
				CURRENT_TIMESTAMP,
				awsRequestId
				 FROM `participant_plan_item` 
				  WHERE 
				 `participant_plan_item`.`participant_plan_item_id`=planItemId;
                 
			 
            select due_date  into @dueDate from plan_item_recurrence where plan_item_id= planItemId and recurrence_id=recurrenceId;
            select  pokket_user_id into @pokketUserId from participant_plan_item where participant_plan_item_id= planItemId;
            if (str_to_date(@dueDate,'%m/%d/%Y %h:%i %p') < current_timestamp ) then
				update participant set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId;
                
				update participant_task_counts set count_of_late_tasks= count_of_late_tasks-1, 
                modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP
                where pokket_user_id=@pokketUserId
                and shared_with_pokket_user_id in (select pokket_user_id from 
                facility_service_case_manager where service_id in (
                select shared_with from pokket_user_share where shared_ref_id=planItemId
                ));

            end if;

                 
          end if;
          
          
          
          select recurrences_count into @recurrencesCount from participant_plan_item where
          participant_plan_item_id=planItemId;
          
          select count(*) into @totalCompletedRecurrences from plan_item_recurrence where
          status_id= 3 and
          `plan_item_id` = planItemId;  
            
            if @recurrencesCount=@totalCompletedRecurrences then
				UPDATE `participant_plan_item`
				SET
				`completed_by` = modifiedBy,
                `completion_date` = completionDate,
				`status_id` = 3,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId 
				WHERE `participant_plan_item_id` = planItemId;
            end if;
   end if;
   
   if @isMandatory = 1 then
   
		select concat(first_name,' ',last_name) into @pokketUserFullName
							from pokket_user where pokket_user_id=@participantPokketUserId;
		select DISTINCT participant_referral.case_manager_id into @currentCaseManagerId
			FROM mypokket.participant_referral
			JOIN facility_service_case_manager
				ON (participant_referral.case_manager_id = facility_service_case_manager.pokket_user_id
				AND participant_referral.service_id = facility_service_case_manager.service_id)
			WHERE participant_referral.pokket_user_id = @participantPokketUserId
			AND participant_referral.is_service_assigned_to_participant = 1
			AND participant_referral.service_id = @serviceId
			AND facility_service_case_manager.is_active = 1;
		
        IF @participantPokketUserId != modifiedBy THEN
			select concat(first_name,' ',last_name, ' (on behalf of ', @pokketUserFullName, ')') into @pokketUserFullName
				from pokket_user where pokket_user_id = modifiedBy;
		END IF;
		select REPLACE(REPLACE(notification_text_template,'<PARTICIPANT_NAME>',@pokketUserFullName),'<PLAN_ITEM_DESC>',@planItemDesc)
			into @notificationTextTemplate from ref_notification_type where notification_type_id=17;     
		
		INSERT INTO `notification`
			(`notification_type_id`,
			`notification_batch_id`,
			 `pokket_user_id`,
			 `notification_from`,
			 `organization_service_id`,
			`notification_text`,
			`notification_dtm`,
			`action_type_id`,
			`action_ref_id`,
			`action_ref_recurrence_id`,
			`is_notified`,
			`is_read`,
			`created_by`,
			`created_dtm`,
			`aws_request_id`)
			values (
			17,
			uuid(),
			@currentCaseManagerId,
			@participantPokketUserId,
			@serviceId,
			@notificationTextTemplate,
			CURRENT_TIMESTAMP,
			6,
			planItemId,
			recurrenceId,
			0,
			0,
			modifiedBy,
			CURRENT_TIMESTAMP,
			awsRequestId
			);
    end if;
    
   COMMIT;
   
    CALL uspGetParticipantPlanItem(planItemId,recurrenceId,modifiedBy,awsRequestId);
	
   
  

END ;;
