CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspOnboardServiceProvider`(

		organizationId varchar(50),

        onboardingOrgId varchar(50),

        organizationName varchar(100),

        jurisdictionCode varchar(50),

        contractId varchar(50),

        isLeadAgency tinyint(1),

        isCriminalJusticeAgency tinyint(1),

        orgRoleIds varchar(100),

        createdBy varchar(50),

        awsRequestId varchar(50),

        organizationWebsite varchar(200))
BEGIN

	DECLARE formattedOrgRoleIds varchar(250);

    DECLARE procError varchar(1000);

    DECLARE displayError varchar(1000);

    DECLARE EXIT HANDLER FOR SQLEXCEPTION

	BEGIN

		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 

			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;

			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);

            SET procError=@full_error;

            SET displayError='';

        ROLLBACK;

        select procError,displayError;

 	END;

    

    START TRANSACTION;

    

    select count(*) into @existingOrgCount from organization

    where organization_id=organizationId;

    

    select count(*) into @existingOnboardedOrgCount from 

    organization_onboarding

    where organization_id=organizationId

    and onboarded_by_org_id=onboardingOrgId;

    

    

    

    select max(rgt) into @rgt from organization where organization_id in (

   select organization_id from organization_onboarding where onboarded_by_org_id =onboardingOrgId

   and organization_id !=onboardingOrgId);

   

   if @rgt is null then 

	select lft into @rgt from organization where organization_id=onboardingOrgId;

   end if;

   

   

   

   UPDATE organization SET rgt = rgt + 2,modified_by=createdBy,

	   modified_dtm=CURRENT_TIMESTAMP WHERE rgt > @rgt;

   UPDATE organization SET lft = lft + 2,modified_by=createdBy,

	   modified_dtm=CURRENT_TIMESTAMP WHERE lft > @rgt;

    

    if @existingOrgCount=0 then

		INSERT INTO `organization`

		(`organization_id`,

		`organization_name`,

		`organization_type_id`,

		`organization_status_id`,

		`jurisdiction_code`,

		`contract_id`,

		`is_lead_agency`,

		`is_cja`,

		`is_non_participating`,

        `lft`,

        `rgt`,

		`created_by`,

		`created_dtm`,

		`aws_request_id`,

        `organization_website`)

		VALUES

		(organizationId,

		organizationName,

		2,

		4,

		jurisdictionCode,

		contractId,

		isLeadAgency,

		isCriminalJusticeAgency,

		1,

        @rgt + 1, 

        @rgt + 2,

		createdBy,

		CURRENT_TIMESTAMP,

		awsRequestId,

        organizationWebsite);

        

   ELSE

   

	   UPDATE organization

	   set organization_status_id=4,

	   jurisdiction_code=jurisdictionCode,

	   contract_id=contractId,

	   is_lead_agency=isLeadAgency,

	   is_cja=isCriminalJusticeAgency,

       lft=@rgt + 1,

       rgt=@rgt + 2,

	   aws_request_id=awsRequestId,

	   modified_by=createdBy,

	   modified_dtm=CURRENT_TIMESTAMP,

       organization_website = organizationWebsite

	   where organization_id=organizationId;

   

        

   END IF;		

   

   if @existingOnboardedOrgCount=0 then

		INSERT INTO `organization_onboarding`

		(`organization_onboarding_id`,

		`organization_id`,

		`onboarded_by_org_id`,

		`sp_invite_status_id`,

		`created_by`,

		`created_dtm`,

		`aws_request_id`)

		VALUES

		(uuid(),

		organizationId,

		onboardingOrgId,

		3, 

		createdBy,

		CURRENT_TIMESTAMP,

		awsRequestId);

        

   else

		UPDATE `organization_onboarding`

		SET

		`sp_invite_status_id` = 3, 

		`modified_by` = createdBy,

		`modified_dtm` = CURRENT_TIMESTAMP,

		`aws_request_id` = awsRequestId

		WHERE organization_id=organizationId

		and onboarded_by_org_id=onboardingOrgId;

   end if;

   

    

    delete from organization_role where organization_id=organizationId;

     select formatCSL(orgRoleIds) into formattedOrgRoleIds; 

 		SET @separator = ',';

		SET @separatorLength = CHAR_LENGTH(@separator);

 		WHILE formattedOrgRoleIds != '' > 0 DO

			SET @orgRoleId = SUBSTRING_INDEX(formattedOrgRoleIds, @separator, 1);

 			INSERT INTO `organization_role`

				(`organization_id`,

				`org_role_id`,

				`created_by`,

				`created_dtm`,

                `aws_request_id`)

				VALUES

				(organizationId,

				@orgRoleId,

				createdby,

				CURRENT_TIMESTAMP,

                awsRequestId);

			SET formattedOrgRoleIds = SUBSTRING(formattedOrgRoleIds, CHAR_LENGTH(@orgRoleId) + @separatorLength + 1);

		END WHILE; 

    

	

   COMMIT;

   

    

	CALL uspGetOrganizationById(organizationId,null,awsRequestId);

    CALL uspGetOrganizationById(onboardingOrgId,null,awsRequestId);

    CALL uspGetServiceProvidersForOnboardingAgency(onboardingOrgId,awsRequestId);

  



END ;;
