CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspReassignCases`(
    oldCaseManagerId varchar(50),
    newCaseManagerId varchar(50),
    reassignment MEDIUMTEXT,
    newFacilityId varchar(50),
    createdBy varchar(50),
    awsRequestId varchar(50),
    notificationBatchId varchar(50)
