BEGIN
DECLARE procError varchar(1000);
DECLARE displayError varchar(1000);
DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE,
    @errno = MYSQL_ERRNO,
    @text = MESSAGE_TEXT;
SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
SET procError = @full_error;
SET displayError = @full_error;
ROLLBACK;
select procError,
    displayError;
END;
Start Transaction;
SELECT 'Start1';
select organization_name into @agencyName
from organization
    inner join pokket_user_organization on organization.organization_id = pokket_user_organization.organization_id
where pokket_user_organization.pokket_user_id = createdBy;
select concat(first_name, ' ', last_name) into @OldCmFullName
from pokket_user
where pokket_user_id = oldCaseManagerId;
SET @separator = '~';
SET @innerSeparator = '^';
SET @outerSeparator = '|';
SET @separatorLength = CHAR_LENGTH(@separator);
SET @innerSeparatorLength = CHAR_LENGTH(@innerSeparator);
SET @outerSeparatorLength = CHAR_LENGTH(@outerSeparator);
WHILE reassignment != '' > 0 DO
SET @reassignments = SUBSTRING_INDEX(reassignment, @separator, 1);
select CHAR_LENGTH(@reassignments) into @lengthOfreassignment;
SET @participantPokketUserId = SUBSTRING_INDEX(@reassignments, @outerSeparator, 1);
SET @reassignments = SUBSTRING(
        @reassignments,
        CHAR_LENGTH(@participantPokketUserId) + 1 + @outerSeparator + 1
    );
WHILE @reassignments != '' > 0 DO
SET @inner_reassignments = SUBSTRING_INDEX(@reassignments, @outerSeparator, 1);
select CHAR_LENGTH(@inner_reassignments) into @lengthOfinnerreassignments;
WHILE @inner_reassignments != '' > 0 DO
SET @oldFacilityId = SUBSTRING_INDEX(@inner_reassignments, @innerSeparator, 1);
SET @inner_reassignments = SUBSTRING(
        @inner_reassignments,
        CHAR_LENGTH(@oldFacilityId) + 1 + @innerSeparator + 1
    );
SET @serviceId = SUBSTRING_INDEX(@inner_reassignments, @innerSeparator, 1);
SET @inner_reassignments = SUBSTRING(
        @inner_reassignments,
        CHAR_LENGTH(@serviceId) + @innerSeparator + 1
    );
