        facility_id,
        service_id,
        pokket_user_id,
        is_active,
        aws_request_id,
        created_by,
        created_dtm
    )
values (
        newFacilityId,
        @serviceId,
        newCaseManagerId,
        1,
        awsRequestId,
        createdBy,
        current_timestamp
    );
end if;
select count(*) into @countOfFacilityService
