CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspSearchServices`(
		inputLat float,
        inputLng float,
        needCategoryIds varchar(50),
        radius INT,
        includeVirtual tinyint(1),
		searchState varchar(2)
    )
BEGIN

IF includeVirtual = 0 THEN
	
	SELECT * FROM 
    (SELECT  
    round(69*vincenty(`facility_latitude`,`facility_longitude`,inputLat, inputLng),2) as distance,
	`facility_name` as facilityName,
    `address_line_1` as addressLine1,
	`address_line_2` as addressLine2,
    `address_city` as city,
    `address_state` as state, 
    `address_zip` as zip,
    `organization_service`.`need_category_id` as needCategoryId,
    `need_category_description` as needCategoryDescription,
    `facility_latitude` as lat,
    `facility_longitude` as lng,
    `phone_number` as phoneNumbers,
    `organization`.`organization_id` as organizationId,
    `organization`.`organization_name` as orgName,
    `organization`.`organization_website` as website,
    if(`organization`.`is_non_participating` =0,true,false) as isPokketParticipating,
    `facility_service`.`facility_id` as facilityId,
    if(`organization_facility`.`is_virtual` = 1,true,false) as facilityIsVirtual,
    `organization_service`.`organization_service_id` as serviceId,
    `organization_service`.`service_name` as serviceName,
    `organization_service`.`service_type` as serviceType,
	`organization_service`.`service_area` as serviceArea,
    `organization_service`.`service_description` as serviceDescription,
	`organization_service`.`service_contact_info` as serviceContactInfo    
 FROM 
 `facility_service` inner join
 `organization_facility` on 
 `facility_service`.`facility_id`=`organization_facility`.`organization_facility_id`
 inner join
 `organization_service` on 
 `facility_service`.`service_id`=`organization_service`.`organization_service_id`
 inner join `organization` on
	 `organization_service`.`organization_id`=`organization`.`organization_id`
 inner join `ref_need_category` 
 on `ref_need_category`.`need_category_id`=`organization_service`.`need_category_id`
 WHERE FIND_IN_SET(`organization_service`.`need_category_id`, needCategoryIds)
 and `organization_facility`.`facility_status_id`= 2 and `organization_service`.`service_status_id` =2 
 and `organization`.`organization_status_id`=4 
 and organization_facility.is_virtual <= includeVirtual
 and `organization_service`.`is_searchable` = 1
 ) A
 where distance <=radius 
ORDER BY isPokketParticipating DESC, distance ASC;

ELSE
	
	SELECT DISTINCT * FROM 
    (SELECT DISTINCT
    round(69*vincenty(`facility_latitude`,`facility_longitude`,inputLat, inputLng),2) as distance,
	`facility_name` as facilityName,
    `address_line_1` as addressLine1,
	`address_line_2` as addressLine2,
    `address_city` as city,
    `address_state` as state, 
    `address_zip` as zip,
    `organization_service`.`need_category_id` as needCategoryId,
    `need_category_description` as needCategoryDescription,
    `facility_latitude` as lat,
    `facility_longitude` as lng,
    `phone_number` as phoneNumbers,
    `organization`.`organization_id` as organizationId,
    `organization`.`organization_name` as orgName,
    `organization`.`organization_website` as website,
    if(`organization`.`is_non_participating` =0,true,false) as isPokketParticipating,
    `facility_service`.`facility_id` as facilityId,
    if(`organization_facility`.`is_virtual` = 1,true,false) as facilityIsVirtual,
    `organization_service`.`organization_service_id` as serviceId,
    `organization_service`.`service_name` as serviceName,
    `organization_service`.`service_type` as serviceType,
	`organization_service`.`service_area` as serviceArea,
    `organization_service`.`service_description` as serviceDescription,
	`organization_service`.`service_contact_info` as serviceContactInfo
 FROM 
 `facility_service` inner join
 `organization_facility` on 
 `facility_service`.`facility_id`=`organization_facility`.`organization_facility_id`
 inner join
 `organization_service` on 
 `facility_service`.`service_id`=`organization_service`.`organization_service_id`
 inner join `organization` on
 `organization_service`.`organization_id`=`organization`.`organization_id`
 inner join `ref_need_category` 
 on `ref_need_category`.`need_category_id`=`organization_service`.`need_category_id`
 WHERE `organization_facility`.`facility_status_id`= 2 and `organization_service`.`service_status_id` =2 
 and `organization`.`organization_status_id`=4 
 and organization_facility.is_virtual <= includeVirtual
 and `organization_service`.`is_searchable` = 1
 and FIND_IN_SET(`organization_service`.`need_category_id`, needCategoryIds)
 ) A
 where distance <=radius
 
	 
		 
		 
		 
 UNION
 SELECT DISTINCT 
	null as distance,
	A.facilityName,
    A.addressLine1,
	A.addressLine2,
    A.city,
    A.state, 
    A.zip,
    A.needCategoryId,
    A.needCategoryDescription,
    A.lat,
    A.lng,
    A.phoneNumbers,
    A.organizationId,
    A.orgName,
    A.website,
    A.isPokketParticipating,
    A.facilityId,
    A.facilityIsVirtual,
    A.serviceId,
    A.serviceName,
    A.serviceType,
	A.serviceArea,
    A.serviceDescription,
	A.serviceContactInfo
	FROM 
    (SELECT DISTINCT 
    F.facilityDistance as facilityDistance,
	virtualFacility.`facility_name` as facilityName,
    virtualFacility.`address_line_1` as addressLine1,
	virtualFacility.`address_line_2` as addressLine2,
    virtualFacility.`address_city` as city,
    virtualFacility.`address_state` as state, 
    virtualFacility.`address_zip` as zip,
    `organization_service`.`need_category_id` as needCategoryId,
    `need_category_description` as needCategoryDescription,
    virtualFacility.`facility_latitude` as lat,
    virtualFacility.`facility_longitude` as lng,
    virtualFacility.`phone_number` as phoneNumbers,
    `organization`.`organization_id` as organizationId,
    `organization`.`organization_name` as orgName,
    `organization`.`organization_website` as website,
    if(`organization`.`is_non_participating` =0,true,false) as isPokketParticipating,
    virtualFacility.`organization_facility_id` as facilityId,
    if(virtualFacility.`is_virtual` = 1,true,false) as facilityIsVirtual,
    `organization_service`.`organization_service_id` as serviceId,
    `organization_service`.`service_name` as serviceName,
    `organization_service`.`service_type` as serviceType,
	`organization_service`.`service_area` as serviceArea,
    `organization_service`.`service_description` as serviceDescription,
	`organization_service`.`service_contact_info` as serviceContactInfo,
    S.serviceStates as serviceStates
 FROM 
 `facility_service`
 inner join
 `organization_service` on 
 `facility_service`.`service_id`=`organization_service`.`organization_service_id`
 inner join `organization` on
 `organization_service`.`organization_id`=`organization`.`organization_id`
 inner join `ref_need_category` 
 on `ref_need_category`.`need_category_id`=`organization_service`.`need_category_id`
 inner join `organization_facility` virtualFacility
 on (`facility_service`.`facility_id`=virtualFacility.`organization_facility_id`
 AND virtualFacility.`is_virtual` = 1)
 left join (select service_id, GROUP_CONCAT(DISTINCT `service_state`.`state_cd`) AS serviceStates
	from service_state WHERE `service_state`.`is_active` =1 group by service_id) S on
    `organization_service`.`organization_service_id` = S.`service_id`
 left join (select service_id, 
	round(69*vincenty(`organization_facility`.`facility_latitude`,`organization_facility`.`facility_longitude`,inputLat, inputLng),2) as facilityDistance
	from organization_facility
    inner join `facility_service` on `facility_service`.`facility_id`=`organization_facility`.`organization_facility_id`
 ) F on `facility_service`.`service_id` = F.`service_id`
 WHERE virtualFacility.`facility_status_id`= 2
 and `organization_service`.`service_status_id` =2 
 and `organization`.`organization_status_id`=4
 and `organization_service`.`is_searchable` = 1
 and FIND_IN_SET(`organization_service`.`need_category_id`, needCategoryIds)
 ) A
 where facilityDistance <=radius
 or serviceArea = 'NATIONWIDE'
 or (serviceArea = 'STATEWIDE' and INSTR(serviceStates,searchState)>0)
ORDER BY isPokketParticipating DESC, distance ASC, orgName ASC;

END IF;

END ;;
