CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUnassignParticipantServiceAssignment`(
		participantPokketUserId varchar(50),
        facilityId varchar(50),
        serviceId varchar(50),
        modifiedBy varchar(50),
        awsRequestId varchar(50))
BEGIN
   
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
            set displayError= @full_error;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
				select case_manager_id into @cmId from participant_referral where
				`pokket_user_id` = participantPokketUserId 
                AND `facility_id` = facilityId
                AND `service_id` = serviceId;
                
   

				UPDATE `participant_referral`
				SET
				`is_service_assigned_to_participant` = 0,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
                `aws_request_id`=awsRequestId
				WHERE `pokket_user_id` = participantPokketUserId 
                AND `facility_id` = facilityId
                AND `service_id` = serviceId;
                
                
                    select count(*) into @countOfServicesAssignedToParticipantManagedBySameCm
					from participant_referral where
					`pokket_user_id` = participantPokketUserId 
					AND `case_manager_id` = @cmId
					AND `is_service_assigned_to_participant` = 1; 
                    
                    
                    if @countOfServicesAssignedToParticipantManagedBySameCm=0 then
						update pokket_user_connection set is_active=0,
						`modified_by` = modifiedBy,
						`modified_dtm` = CURRENT_TIMESTAMP,
                        `aws_request_id`=awsRequestId
						where pokket_user_id=participantPokketUserId and 
						connection_pokket_user_id=@cmId and connection_pokket_user_role_id=4;
					
						update pokket_user_connection set is_active=0,
						`modified_by` = modifiedBy,
						`modified_dtm` = CURRENT_TIMESTAMP,
                        `aws_request_id`=awsRequestId
						where pokket_user_id=@cmId and 
						connection_pokket_user_id=participantPokketUserId and connection_pokket_user_role_id=5;
                
                end if;


				
                
        
     COMMIT;  


END ;;
