CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUnshare`(
		sharedByPokketUserId varchar(50),
		sharedWithServiceId varchar(50),
        sharedRefId varchar(50),
        shareTypeId tinyint(4),
        needCategoryId tinyint(4),
        notificationBatchId varchar(50),
		modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    
    START TRANSACTION;
    
     if shareTypeId=1  then
          UPDATE `pokket_user_share` set is_active=0,
					modified_by=modifiedBy,
					modified_dtm=CURRENT_TIMESTAMP,
					`aws_request_id`=awsRequestId
					where `shared_by`= sharedByPokketUserId
                    and `shared_with`= sharedWithServiceId
                    and share_type_id NOT IN (4,5) and is_mandatory !=1;

             UPDATE `pokket_user_share` set is_active=0,
					modified_by=modifiedBy,
					modified_dtm=CURRENT_TIMESTAMP,
					`aws_request_id`=awsRequestId
					where `shared_by`= sharedByPokketUserId
                    and `shared_with`= sharedWithServiceId
                    and share_type_id =4 and shared_ref_id in (
                    select document_id from plan_item_document where participant_plan_item_id in
                    (select participant_plan_item_id from participant_plan_item where pokket_user_id=sharedByPokketUserId
                    and is_mandatory !=1));
     end if;
    
     if shareTypeId=2  then 
			select count(*) into @countOfActiveWholePlanShares from vw_active_pokket_user_shares
				where `shared_by`= sharedByPokketUserId
                    and `shared_with`= sharedWithServiceId;  
    
			
					UPDATE `pokket_user_share` set is_active=0,
						modified_by=modifiedBy,
						modified_dtm=CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId
						where `shared_by`= sharedByPokketUserId
						and `shared_with`= sharedWithServiceId
                        and `need_category_id`=needCategoryId;
			
     end if;
     
       if shareTypeId=3  then 						
			select parent_plan_item_id into @parentPlanItem from participant_plan_item where
				participant_plan_item_id=sharedRefId;
						
			select parent_plan_item_id into @parentParentPlanItem from participant_plan_item where
				participant_plan_item_id=@parentPlanItem;
						
			select count(*) into @countofSharedParentPlanItem 
				from pokket_user_share where 
				shared_by= sharedByPokketUserId and 
				shared_ref_id=@parentPlanItem
				and shared_with=sharedWithServiceId and is_active=0;
						
			select count(*) into @countofSharedParentParentPlanItem 
				from pokket_user_share where 
				shared_by= sharedByPokketUserId and 
				shared_ref_id=@parentParentPlanItem
				and shared_with=sharedWithServiceId and is_active=0;
                        
			select count(*) into @countOfExistingShares from pokket_user_share
				where shared_by= sharedByPokketUserId and 
				shared_with=sharedWithServiceId 
				and shared_ref_id =sharedRefId;

			if @countofSharedParentPlanItem =0 
				and @countofSharedParentParentPlanItem=0  
				and @countOfExistingShares=0 then
						
				INSERT INTO `pokket_user_share`
					(`share_id`,
					`shared_by`,
					`shared_with`,
					`shared_ref_id`,
					`share_start_dtm`,
					`share_type_id`,
					`share_duration_id`,
					`share_duration_units`,
					`need_category_id`,
					`is_active`,
					`is_accepted`,
					`created_by`,
					`created_dtm`,`aws_request_id`
					)
					select UUID(),
					sharedByPokketUserId,
					sharedWithServiceId,
					participant_plan_item_id,
					CURRENT_TIMESTAMP,
					shareTypeId,
					6,
					0,
					need_category_id,
					0,0,modifiedBy,CURRENT_TIMESTAMP,awsRequestId 
					FROM `participant_plan_item`
					WHERE  `participant_plan_item_id`=sharedRefId
					and `participant_plan_item`.`pokket_user_id`=sharedByPokketUserId;
					
			 end if;       
	
        UPDATE `pokket_user_share` set is_active=0,
						modified_by=modifiedBy,
						modified_dtm=CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId
						where `shared_by`= sharedByPokketUserId
						and `shared_with`= sharedWithServiceId
                        and `shared_ref_id`=sharedRefId
                        and is_mandatory !=1;
                        
		 UPDATE `pokket_user_share` set is_active=0,
						modified_by=modifiedBy,
						modified_dtm=CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId
						where `shared_by`= sharedByPokketUserId
						and `shared_with`= sharedWithServiceId
                        and `shared_ref_id` in (select participant_plan_item_id  from participant_plan_item where
						parent_plan_item_id=sharedRefId)
                        and is_mandatory !=1;
                        
		 UPDATE `pokket_user_share` set is_active=0,
						modified_by=modifiedBy,
						modified_dtm=CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId
						where `shared_by`= sharedByPokketUserId
						and `shared_with`= sharedWithServiceId
                        and `shared_ref_id` in (select participant_plan_item_id  from participant_plan_item where
						parent_plan_item_id in (select participant_plan_item_id  from participant_plan_item where
						parent_plan_item_id=sharedRefId))
                        and is_mandatory !=1;
       
       end if;
       
       if shareTypeId=4  then 
       
			select count(*) into @countOfExistingShares from pokket_user_share
							where shared_by= sharedByPokketUserId and 
							shared_with=sharedWithServiceId 
							and shared_ref_id =sharedRefId;
                        
			if  @countOfExistingShares=0 then
				INSERT INTO `pokket_user_share`
					(`share_id`,
					`shared_by`,
					`shared_with`,
					`shared_ref_id`,
					`share_start_dtm`,
					`share_type_id`,
					`share_duration_id`,
					`share_duration_units`,
					`need_category_id`,
					`is_active`,
					`is_accepted`,
					`created_by`,
					`created_dtm`,`aws_request_id`
					)
					VALUES(
					UUID(),
					sharedByPokketUserId,
					sharedWithServiceId,
					sharedRefId,
					CURRENT_TIMESTAMP,
					shareTypeId,
					6,
					0,
					null,
					0,0,modifiedBy,CURRENT_TIMESTAMP,awsRequestId );
			 end if;                                  
       
			UPDATE `pokket_user_share` set is_active=0,
						modified_by=modifiedBy,
						modified_dtm=CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId
						where `shared_by`= sharedByPokketUserId
						and `shared_with`= sharedWithServiceId
                        and `shared_ref_id`=sharedRefId;
       end if;

	if shareTypeId=5  then 
      
		UPDATE `pokket_user_share` set is_active=0,
			modified_by=modifiedBy,
			modified_dtm=CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId
			where `shared_by`= sharedByPokketUserId
			and `shared_with`= sharedWithServiceId
			and share_type_id = shareTypeId;
	end if;

	if shareTypeId=6  then 
		
		select count(*) into @countOfExistingShares from pokket_user_share
							where shared_by= sharedByPokketUserId and 
							shared_with=sharedWithServiceId 
							and shared_ref_id =sharedRefId;
                            
		if  @countOfExistingShares=0 then
			INSERT INTO `pokket_user_share`
				(`share_id`,
				`shared_by`,
				`shared_with`,
				`shared_ref_id`,
				`share_start_dtm`,
				`share_type_id`,
				`share_duration_id`,
				`share_duration_units`,
				`need_category_id`,
				`is_active`,
				`is_accepted`,
				`created_by`,
				`created_dtm`,`aws_request_id`
				)
				VALUES(
				UUID(),
				sharedByPokketUserId,
				sharedWithServiceId,
				sharedRefId,
				CURRENT_TIMESTAMP,
				shareTypeId,
				6,
				0,
				null,
				0,0,modifiedBy,CURRENT_TIMESTAMP,awsRequestId );
		 end if;                             

		
		
		UPDATE `pokket_user_share` 
		set 
			is_active=0,
			share_type_id=shareTypeId,
			modified_by=modifiedBy,
			modified_dtm=CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId
			where `shared_by`= sharedByPokketUserId
			and `shared_with`= sharedWithServiceId
			and `shared_ref_id`=sharedRefId;
	end if;

	
    select concat(first_name,' ',last_name) into @participantFullName
				from pokket_user where pokket_user_id=sharedByPokketUserId;
                
	if shareTypeId=6 then 
	
		set @notificationTypeId=41; 
	else 
		set @notificationTypeId=10; 
	end if;   

	IF sharedByPokketUserId != modifiedBy THEN
		select concat(first_name,' ',last_name, ' (on behalf of ', @participantFullName, ')') into @participantFullName
			from pokket_user where pokket_user_id = modifiedBy;
	END IF;
	select REPLACE(notification_text_template,'<PARTICIPANT_NAME>',@participantFullName)
			into @notificationUnshareTextTemplate from ref_notification_type where notification_type_id = @notificationTypeId;

	INSERT INTO `notification`
		(`notification_type_id`,
		`notification_batch_id`,
		 `pokket_user_id`,
		 `notification_from`,
		 `organization_service_id`,
		 `email_address`,
		`notification_text`,
		`notification_dtm`,
		`action_type_id`,
		`action_ref_id`,
		`is_notified`,
		`is_read`,
		`created_by`,
		`created_dtm`,`aws_request_id`)
	select  
        @notificationTypeId,
        notificationBatchId,
		facility_service_case_manager.pokket_user_id,
		sharedByPokketUserId,
        sharedWithServiceId,
		pokket_user.email_address,
		@notificationUnshareTextTemplate,
		CURRENT_TIMESTAMP,
        3,
        sharedByPokketUserId,
		0,0,
        modifiedBy,
        CURRENT_TIMESTAMP,
        awsRequestId 
	from facility_service_case_manager inner join pokket_user
		on facility_service_case_manager.pokket_user_id=pokket_user.pokket_user_id
		where facility_service_case_manager.service_id =sharedWithServiceId and
		facility_service_case_manager.is_active=1;
    
COMMIT;

END ;;
