CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateAppointmentRequestStatus`(
		appointmentRequestId varchar(50),
        appointmentRequestStatusId tinyint(4),
        timeSlotId INT(11),
        eventId varchar(50),
        participantPlanItemId varchar(50),
        acceptNote varchar(1000),
        rejectNote varchar(1000),
        modifiedBy varchar(50),
        notificationBatchId varchar(50),
        awsRequestId varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE appointmentInstanceId varchar(50);
    DECLARE toParticipantPokketUserId varchar(50);
    DECLARE finished INTEGER DEFAULT 0;
    DECLARE curCancelAppointment
				CURSOR FOR 
					SELECT `appointment_request`.`appointment_request_id`,
						`appointment_request`.`to_pokket_user_id`
					FROM `mypokket`.`appointment_request` 
                    WHERE `appointment_request`.`appointment_batch_id` = appointmentRequestId  
                    AND `appointment_request`.`appointment_request_id` != appointmentRequestId; 
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1; 
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
