CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateDependentPhoneNumber`(
lambdaArn varchar(500)
)
BEGIN
    DECLARE finished INTEGER DEFAULT 0;
    DECLARE awsRequestId varchar(50) DEFAULT '-NA-';
    DECLARE guardianPhone varchar(25);
	DECLARE dependentId VARCHAR(50);
	DECLARE dependentFirstName VARCHAR(75);
	DECLARE dependentLastName VARCHAR(75);
	DECLARE isPrimaryGuardian TINYINT(1);
	DECLARE guardian_pokket_user_id VARCHAR(50);
	DECLARE dependent_pokket_user_id VARCHAR(50);

    DECLARE curBulkUpdate
        CURSOR FOR 
            SELECT 
            `guardian_dependent_import`.`guardianPhone`,
			`guardian_dependent_import`.`dependentId`,
			`guardian_dependent_import`.`dependentFirstName`,
			`guardian_dependent_import`.`dependentLastName`,
			`guardian_dependent_import`.`isPrimaryGuardian`,
			`guardian_dependent_import`.`guardian_pokket_user_id`,
			`guardian_dependent_import`.`dependent_pokket_user_id`
			FROM `mypokket`.`guardian_dependent_import`;
    
    DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET finished = 1;
 
    OPEN curBulkUpdate;
 
    getRecord: LOOP
        FETCH curBulkUpdate INTO 
        guardianPhone,
		dependentId,
		dependentFirstName,
		dependentLastName,
		isPrimaryGuardian,
		guardian_pokket_user_id,
		dependent_pokket_user_id;
        
        IF finished = 1 THEN 
            LEAVE getRecord;
        END IF;
        
        IF isPrimaryGuardian = 1 THEN 
			SELECT user_name INTO @dependentUserName FROM pokket_user WHERE pokket_user_id = dependent_pokket_user_id;
			
			CALL mysql.lambda_async(lambdaArn, 
				CONCAT('{ 
						"userName" : "', @dependentUserName, 
						'", "operationName" : ', '"adminupdateuserattributes"',
						', "userAttributes" : ', '[
							{"Name":', '"phone_number"',',','"Value":', '"',guardianPhone,'"','},
							{"Name":', '"phone_number_verified"',',','"Value":', '"true"','}',
						']',
						'}')
			);
        END IF;
	END LOOP getRecord;
	CLOSE curBulkUpdate;
    
 
END ;;
