CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateGuardianDependent`(newGuardiaUserName varchar(75),dependentUserName varchar(75),oldGuardianUserName varchar(75), spaUserName varchar(75), lambdaArn varchar(500))
BEGIN 

	

	set @newGuardianPokketUserId = (select pokket_user_id from pokket_user pu where pu.user_name = newGuardiaUserName );

	set @oldGuardianPokketUserId = (select pu2.pokket_user_id from pokket_user pu2 where pu2.user_name = oldGuardianUserName );

	select pu3.pokket_user_id, pu3.first_name, pu3.last_name into @dependentPokketUserId, @dependentFirstName, @dependentLastName from pokket_user pu3 where pu3.user_name = dependentUserName;

	set @spaPokketUserId = (select pu4.pokket_user_id from pokket_user pu4 where pu4.user_name = spaUserName );

	select count(*) into @isConnected from guardian_dependent gd where gd.guardian_id = @newGuardianPokketUserId and gd.dependent_id = @dependentPokketUserId; 



if @isConnected = 0 then



	

INSERT INTO guardian_dependent

      (guardian_id, 

      dependent_id,

      is_active,

      created_by,

      created_dtm)

      VALUES(

      @newGuardianPokketUserId,

      @dependentPokketUserId,

      1,

      @spaPokketUserId,

      CURRENT_TIMESTAMP);

     

else 

	UPDATE guardian_dependent

SET is_active=1, modified_by=@spaPokketUserId, modified_dtm=CURRENT_TIMESTAMP

WHERE guardian_id=@newGuardianPokketUserId AND dependent_id=@dependentPokketUserId;



end if;

	



UPDATE guardian_dependent

SET is_active=0, modified_by=@spaPokketUserId, modified_dtm=CURRENT_TIMESTAMP

WHERE guardian_id=@oldGuardianPokketUserId AND dependent_id=@dependentPokketUserId;



UPDATE participant

INNER JOIN

guardian_dependent 

ON (participant.pokket_user_id = @dependentPokketUserId

AND guardian_dependent.is_active = 1)

SET participant.modified_by=@spaPokketUserId, 

participant.modified_dtm=CURRENT_TIMESTAMP(), 

participant.primary_guardian_id = @newGuardianPokketUserId;



INSERT INTO mypokket.notification

	(notification_type_id,notification_batch_id,pokket_user_id, notification_text, notification_dtm, notification_from, created_by, created_dtm, aws_request_id)

	VALUES(

	48, uuid(), @oldGuardianPokketUserId, CONCAT(@dependentFirstName,' ', @dependentLastName, ' has been removed from underneath your guardianship.' ), CURRENT_TIMESTAMP, @spaPokketUserId, @spaPokketUserId, CURRENT_TIMESTAMP, '-NA-');



INSERT INTO mypokket.notification

	(notification_type_id,notification_batch_id,pokket_user_id, notification_text, notification_dtm, notification_from, created_by, created_dtm, aws_request_id)

	VALUES(

	47, uuid(), @newGuardianPokketUserId, CONCAT(@dependentFirstName,' ', @dependentLastName, ' has been added to underneath your guardianship.' ), CURRENT_TIMESTAMP, @spaPokketUserId, @spaPokketUserId, CURRENT_TIMESTAMP, '-NA-');



call uspUpdateDependentPhoneNumber(lambdaArn);



END ;;
