CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateGuardianDependentImport`(spaPhoneNumber varchar(50),lambdaArn varchar(500))
BEGIN 

	DECLARE import_finished INTEGER Default 0; 

	DECLARE import_guardianPhone varchar(25) default null;

    DECLARE import_dependentId varchar(50) default null;

    DECLARE import_dependentFirstName varchar(75) default null;

    DECLARE import_dependentLastName varchar(75) default null;

    DECLARE import_isPrimaryGuardian tinyint(1) default null;

    DECLARE import_guardian_pokket_user_id varchar(50) default null;

    DECLARE import_dependent_pokket_user_id varchar(50) default null;

   

   

   DECLARE import_gaurdian_dependent

   	CURSOR FOR

   		SELECT 

   			gdi.guardianPhone,

   			gdi.dependentId,

   			gdi.dependentFirstName,

   			gdi.dependentLastName,

   			gdi.isPrimaryGuardian,

   			gdi.guardian_pokket_user_id,

   			gdi.dependent_pokket_user_id

   		FROM

   		mypokket.guardian_dependent_import gdi 

   		WHERE gdi.dependent_pokket_user_id is not null;

   

   	DECLARE CONTINUE HANDLER FOR NOT FOUND SET import_finished = 1;

   

   UPDATE guardian_dependent_import

 	SET guardianPhone = CONCAT( '+1', guardianPhone);

 

 	UPDATE guardian_dependent_import

		INNER JOIN

			pokket_user 

		ON guardian_dependent_import.guardianPhone = pokket_user.primary_phone_number

	SET guardian_dependent_import.guardian_pokket_user_id = pokket_user.pokket_user_id;



	UPDATE guardian_dependent_import

		INNER JOIN

			pokket_user 

		ON guardian_dependent_import.dependentId = pokket_user.ssn

		SET guardian_dependent_import.dependent_pokket_user_id = pokket_user.pokket_user_id

		where guardian_dependent_import.dependentId is not null and guardian_dependent_import.dependentId != '';



	

	UPDATE guardian_dependent_import

		INNER JOIN

			pokket_user 

	ON guardian_dependent_import.dependent_pokket_user_id = pokket_user.pokket_user_id

	SET guardian_dependent_import.dependent_pokket_user_id = pokket_user.pokket_user_id, guardian_dependent_import.dependentFirstName = pokket_user.first_name, guardian_dependent_import.dependentLastName = pokket_user.last_name

	WHERE guardian_dependent_import.dependent_pokket_user_id = pokket_user.pokket_user_id;



	SET @spaPokketUserId = (

  	SELECT pu2.pokket_user_id from pokket_user pu2 where pu2.primary_phone_number = spaPhoneNumber);

  

  



   		

   	OPEN import_gaurdian_dependent;

   

   	input_gd_import: LOOP

   		FETCH import_gaurdian_dependent

   		into 

   			import_guardianPhone,

   			import_dependentId,

   			import_dependentFirstName,

   			import_dependentLastName,

   			import_isPrimaryGuardian,

   			import_guardian_pokket_user_id,

   			import_dependent_pokket_user_id;

   		

   	IF import_finished = 1 THEN 

          LEAVE input_gd_import;

      END IF;

   	

   	select 

   	@spaPokketUserId, lambdaArn,import_guardianPhone,

   			import_dependentId,

   			import_dependentFirstName,

   			import_dependentLastName,

   			import_isPrimaryGuardian,

   			import_guardian_pokket_user_id,

   			import_dependent_pokket_user_id;

   		

     call mypokket.uspInsertGuardianDependent (@spaPokketUserId,lambdaArn, import_guardianPhone,import_dependentId, import_dependentFirstName, import_dependentLastName, import_isPrimaryGuardian, import_guardian_pokket_user_id, import_dependent_pokket_user_id);

	

   	END LOOP input_gd_import;

   	CLOSE import_gaurdian_dependent;



END ;;
