BEGIN
    
  
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    
    
	START TRANSACTION;
    
    
    UPDATE `organization` 
	SET `organization_name` = organizationName,
		 `modified_by` = modifiedBy,
		 `modified_dtm` = CURRENT_TIMESTAMP
	WHERE `organization_id` = organizationId;
    
    select count(*) into @physicalAddressCount from organization_address
    where organization_id=organizationId and address_type_id=1;
    select count(*) into @mailingAddressCount from organization_address
    where organization_id=organizationId and address_type_id=2;
    
    IF(physicalAddressLine1 IS NOT NULL) THEN
		if @physicalAddressCount = 0 then
			INSERT INTO `organization_address`
			(`organization_id`,
			`address_type_id`,
			`address_line_1`,
			`address_line_2`,
			`address_city`,
			`address_county`,
			`address_state`,
			`address_zip`,
			`created_by`)
			VALUES
			(organizationId,
			1,
			physicalAddressLine1,
			physicalAddressLine2,
			physicalAddressCity,
			physicalAddressCounty,
			physicalAddressState,
			physicalAddressZip,
			modifiedBy);
		else
			UPDATE `organization_address`
			SET
			`address_line_1` = physicalAddressLine1,
			`address_line_2` = physicalAddressLine2,
			`address_city` = physicalAddressCity,
			`address_county` = physicalAddressCounty,
			`address_state` = physicalAddressState,
			`address_zip` = physicalAddressZip,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP
			WHERE `organization_id` = organizationId AND `address_type_id` = 1;
			
		end if;
    END IF;
    IF(mailingAddressLine1 IS NOT NULL) THEN
		 if @mailingAddressCount = 0 then
				INSERT INTO `organization_address`
				(`organization_id`,
				`address_type_id`,
				`address_line_1`,
				`address_line_2`,
				`address_city`,
				`address_county`,
				`address_state`,
				`address_zip`,
				`created_by`)
				VALUES
				(organizationId,
				2,
				mailingAddressLine1,
				mailingAddressLine2,
				mailingAddressCity,
				mailingAddressCounty,
				mailingAddressState,
				mailingAddressZip,
				modifiedBy);
			else
				UPDATE `organization_address`
				SET
				`address_line_1` = mailingAddressLine1,
				`address_line_2` = mailingAddressLine2,
				`address_city` = mailingAddressCity,
				`address_county` = mailingAddressCounty,
				`address_state` = mailingAddressState,
				`address_zip` = mailingAddressZip,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP
				WHERE `organization_id` = organizationId AND `address_type_id` = 2;
			end if;
    END IF;
    
    
    COMMIT;

END ;;
