        phoneNumber varchar(15),
        latitude varchar(15),
        longitude varchar(15),
        serviceIds mediumtext,
        caseManagerIds mediumtext,
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
    
    DECLARE formattedServiceIds mediumtext;
    DECLARE formattedCaseManagerIds mediumtext;
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'Record already exists.';
                end if;    
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
     
   


    
    START TRANSACTION;
     
     
    UPDATE `organization_facility`
		SET
		`facility_name` = facilityName,
		`facility_operating_hours` = facilityOperatingHours,
