		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
        `aws_request_id`=awsRequestId
		WHERE `organization_facility_id` = organizationFacilityId;
 
		SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);

        
        delete from `facility_service` where `facility_id`=organizationFacilityId;
        select formatCSL(serviceIds) into formattedServiceIds; 
 		WHILE formattedServiceIds != '' > 0 DO
			SET @serviceId = SUBSTRING_INDEX(formattedServiceIds, @separator, 1);
