				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
			SET formattedServiceIds = SUBSTRING(formattedServiceIds, CHAR_LENGTH(@serviceId) + @separatorLength + 1);
		END WHILE;   
        
        
        update `facility_case_manager` set is_active=0,modified_by=modifiedBy,
        modified_dtm=current_timestamp,aws_request_id=awsRequestId where `facility_id`=organizationFacilityId;
        
        update facility_service_case_manager set is_active=0 ,modified_by=modifiedBy,
        modified_dtm=current_timestamp,aws_request_id=awsRequestId where `facility_id`=organizationFacilityId
        and pokket_user_id in (select pokket_user_id from  `facility_case_manager` where `facility_id`=organizationFacilityId);
           
		select formatCSL(caseManagerIds) into formattedCaseManagerIds; 
 		WHILE formattedCaseManagerIds != '' > 0 DO
			SET @caseManagerId = SUBSTRING_INDEX(formattedCaseManagerIds, @separator, 1);
            select count(*) into @countOfCm from facility_case_manager where facility_id=organizationFacilityId
            and pokket_user_id=@caseManagerId;
            if @countOfCm=0 then
 			INSERT INTO `facility_case_manager`
				(`facility_id`,
				`pokket_user_id`,
                `is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationFacilityId,
				@caseManagerId,
                1,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
             else
              update `facility_case_manager` set is_active=1 ,modified_by=modifiedBy,
			  modified_dtm=current_timestamp,aws_request_id=awsRequestId
              where `facility_id`=organizationFacilityId and pokket_user_id=@caseManagerId;
             end if;
             update `facility_service_case_manager` set is_active=1 ,modified_by=modifiedBy,
			  modified_dtm=current_timestamp,aws_request_id=awsRequestId
              where `facility_id`=organizationFacilityId and pokket_user_id=@caseManagerId;
			SET formattedCaseManagerIds = SUBSTRING(formattedCaseManagerIds, CHAR_LENGTH(@caseManagerId) + @separatorLength + 1);
		END WHILE;   

	
        
     COMMIT;   
   
   CALL uspGetFacility(organizationFacilityId,awsRequestId);
    

