                `release_date`,
                `requiring_org_id`,
                `service_id`,
                `agreement_text`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(@currentRoiAgreementId,
                @currentRoiVersion+1,
                1,
                'ROI',
                'service_roi',
                CURRENT_TIMESTAMP,
                @organizationId,
                organizationServiceId,
                releaseOfInformationText,
				modifiedBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
		END IF; 
	END IF;  
    
    UPDATE `organization_service`
		SET
		`service_name` = serviceName,
		`need_category_id` = needCategoryId,
		`service_eligibility_min_age` = minimumAge,
		`service_eligibility_max_age` = maximumAge,
		`is_referral_required` = isReferralRequired,
		`service_eligibility_gender_id` = gender,
		`is_available_for_sex_offender` = isAvailableForSexOffender,
		`is_available_for_family_children` = isAvailableForFamilyChildren,
		`release_of_information_text` = releaseOfInformationText,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
        `aws_request_id`=awsRequestId ,
        `referral_doc_requirement`= referralDocRequirement,
        `referral_doc_desc` = referralDocDesc,
        `service_type` = serviceType,
		`service_area` = serviceArea,
        `is_searchable` = isSearchable,
        `service_description` = serviceDescription,
        `service_contact_info` = serviceContactInfo,
        `veteran_only` = veteranOnly
		WHERE `organization_service_id` = organizationServiceId;

        SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        
        delete from `facility_service` where `service_id`=organizationServiceId;
        select formatCSL(facilityIds) into formattedFacilityIds; 
 		WHILE formattedFacilityIds != '' > 0 DO
			SET @facilityId = SUBSTRING_INDEX(formattedFacilityIds, @separator, 1);
 			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(@facilityId,
				organizationServiceId,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
			SET formattedFacilityIds = SUBSTRING(formattedFacilityIds, CHAR_LENGTH(@facilityId) + @separatorLength + 1);
		END WHILE;
        
	IF serviceType = 'VIRTUAL' THEN
		
        
        select organization_facility_id into @virtualFacilityId
        FROM organization_facility
        WHERE is_virtual = 1
        AND organization_id = @organizationId;
        
        IF @virtualFacilityId is null THEN
			SET @newVirtualFacilityId = uuid();
			INSERT INTO `organization_facility`
				(`organization_facility_id`,
				`organization_id`,
				`facility_name`,
				`facility_status_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`,
				`is_virtual`
				)
				VALUES
				(@newVirtualFacilityId,
				@organizationId,
				'VIRTUAL',
				2,
				modifiedBy,
				CURRENT_TIMESTAMP,
				awsRequestId,
				1
				);
			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(@newVirtualFacilityId,
				organizationServiceId,
				modifiedBy,
				CURRENT_TIMESTAMP,
                awsRequestId);
        ELSE
			INSERT INTO `facility_service`
				(`facility_id`,
				`service_id`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
				VALUES
				(@virtualFacilityId,
				organizationServiceId,
				modifiedBy,
				CURRENT_TIMESTAMP,
                awsRequestId)
			ON DUPLICATE KEY UPDATE aws_request_id=awsRequestId;
        END IF;
        
	END IF;
        
        delete from `organization_service_payscale` where `organization_service_id`=organizationServiceId;
        select formatCSL(payscales) into formattedPayscales; 
 		WHILE formattedPayscales != '' > 0 DO
			SET @payScaleId = SUBSTRING_INDEX(formattedPayscales, @separator, 1);
 
			INSERT INTO `organization_service_payscale`
				(`organization_service_id`,
				`pay_scale_id`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@payScaleId,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
			SET formattedPayscales = SUBSTRING(formattedPayscales, CHAR_LENGTH(@payScaleId) + @separatorLength + 1);
		END WHILE;
        
        SET @caretseparator = '^';
		SET @caretSeparatorLength = CHAR_LENGTH(@caretseparator);
        
        delete from `prescreen_question` where `organization_service_id`=organizationServiceId;
        
        select prescreenQuestions into formattedPrescreenQuestions; 
        WHILE formattedPrescreenQuestions != '' > 0 DO
			SET @prescreenQuestionWithAnswer = SUBSTRING_INDEX(formattedPrescreenQuestions, @caretseparator, 1);
			SET @prescreenQuestion = SUBSTRING_INDEX(@prescreenQuestionWithAnswer, '|', 1);
            SET @prescreenQuestionQualifyingAnswer= SUBSTRING_INDEX(@prescreenQuestionWithAnswer, '|', -1);
			INSERT INTO `prescreen_question`
				(`organization_service_id`,
				`prescreen_question_description`,
                `prescreen_question_qualifying_answer`,
                `is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@prescreenQuestion,
                @prescreenQuestionQualifyingAnswer,
                1,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
			SET formattedPrescreenQuestions = SUBSTRING(formattedPrescreenQuestions, CHAR_LENGTH(@prescreenQuestionWithAnswer) + @caretSeparatorLength + 1);
		END WHILE;
        
        delete from `service_intake_question` where `organization_service_id`=organizationServiceId;
        
        select serviceIntakeQuestions into formattedServiceIntakeQuestions; 
        WHILE formattedServiceIntakeQuestions != '' > 0 DO
			SET @serviceIntakeQuestion = SUBSTRING_INDEX(formattedServiceIntakeQuestions, @caretseparator, 1);
			
			INSERT INTO `service_intake_question`
				(`organization_service_id`,
				`service_intake_question_description`,
                `is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(organizationServiceId,
				@serviceIntakeQuestion,
                1,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);

 
			SET formattedServiceIntakeQuestions = SUBSTRING(formattedServiceIntakeQuestions, CHAR_LENGTH(@serviceIntakeQuestion) + @caretSeparatorLength + 1);
		END WHILE;
        
        
		
      
       
        
         delete from `facility_service_case_manager`  where `service_id`=organizationServiceId;
        
     insert into facility_service_case_manager (facility_id,service_id,pokket_user_id,is_active,aws_request_id,
     created_by,created_dtm)
     select facility_service.facility_id,facility_service.service_id,facility_case_manager.pokket_user_id,1,
     awsRequestId,modifiedBy,current_timestamp 
 from facility_service inner join facility_case_manager on
facility_service.facility_id=facility_case_manager.facility_id where 
FIND_IN_SET(`facility_case_manager`.`pokket_user_id`, caseManagerIds) and
facility_service.service_id=organizationServiceId;

	IF serviceArea = 'STATEWIDE' AND serviceStates != ''
	
	THEN
		delete from `service_state`  where `service_id`=organizationServiceId;
		select formatCSL(serviceStates) into formattedServiceStates; 
 		WHILE formattedServiceStates != '' > 0 DO
			SET @stateCd = SUBSTRING_INDEX(formattedServiceStates, @separator, 1);
 			INSERT INTO `service_state`
			(`service_id`,
			`state_cd`,
			`is_active`,
			`created_by`,
			`aws_request_id`)
			VALUES
			(organizationServiceId,
			@stateCd,
			1,
			modifiedBy,
			awsRequestId);
			SET formattedServiceStates = SUBSTRING(formattedServiceStates, CHAR_LENGTH(@stateCd) + @separatorLength + 1);
		END WHILE;
	END IF;
           
		
        
        
     COMMIT;   
    
	CALL uspGetService(organizationServiceId,awsRequestId);
   

END ;;
