CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateParticipantPlanItem`(
        participantPlanItemId varchar(50),
		recurrenceId varchar(50),
        startTime varchar(30),
        recurrencePatternId varchar(50),
        recurrencePatternRule varchar(15000),
        description varchar(150),
        location varchar(200),
        dueDate varchar(30),
        completionDate varchar(30),
        statusId tinyint(4),
        notes varchar(1000),
        needCategoryId tinyint(4),
        didPlanItemMeetNeedOnCompletion tinyint(4),
        repeatInterval tinyint(4),
        repeatTypeId varchar(10),
        servicePlanItemId int(11),
        severityId tinyint(4),
        priority tinyint(4),
        actionTypeId tinyint(4),
        isMandatory tinyint(1),
        modifiedBy varchar(50),
        awsRequestId varchar(50),
        serviceId varchar(50),
        duration tinyint(4))
BEGIN
    DECLARE eventId varchar(50);
    DECLARE pokketUserId varchar(50);
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
   
   
    
    select pokket_user_id,need_category_id into pokketUserId,@existingNeedCategoryId from `participant_plan_item`
    WHERE `participant_plan_item_id` = participantPlanItemId;
    
    select count(*) into @countOfRecurrenceId from `plan_item_recurrence`
        where `recurrence_id` = recurrenceId 
		AND `plan_item_id` = participantPlanItemId;
        
        select repeat_type_id into @repeatTypeId from `participant_plan_item`
    WHERE `participant_plan_item_id` = participantPlanItemId;
    
    update pokket_user_event set 
   starts_at_dtm = STR_TO_DATE(dueDate, '%m/%d/%Y'), 
  ends_at_dtm=  DATE_ADD(STR_TO_DATE(dueDate, '%m/%d/%Y'),INTERVAL duration MINUTE),
  `modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
		`aws_request_id`=awsRequestId 
        where participant_plan_item_id = participantPlanItemId;
    
    if ifnull(@repeatTypeId,'NONE')='NONE'  then
    
    
		UPDATE `participant_plan_item`
		SET
		`participant_plan_item_description` = description,
		`location` = location,
        `start_time`=startTime,
		`due_date` = dueDate,
		`completion_date` = completionDate,
		`status_id` = statusId,
		`notes`=notes,
		`need_category_id` = needCategoryId,
		`is_need_met_after_completion` = didPlanItemMeetNeedOnCompletion,
 		`service_plan_item_id` = servicePlanItemId,
		`severity_id` = severityId,
		`priority` = priority,
		`action_type_id` = actionTypeId,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
		`aws_request_id`=awsRequestId ,
        `is_mandatory`=isMandatory,
        `duration_in_minutes` = duration
		WHERE `participant_plan_item_id` = participantPlanItemId;
    
    else
    
		select recurrence_pattern_id into @recurrencePatternId
        from participant_plan_item where 
        `participant_plan_item_id` = participantPlanItemId;
    
		UPDATE `participant_plan_item`
		SET
		`participant_plan_item_description` = description,
		`need_category_id` = needCategoryId,
 		`service_plan_item_id` = servicePlanItemId,
		`severity_id` = severityId,
		`priority` = priority,
		`action_type_id` = actionTypeId,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
        `is_mandatory`=isMandatory,
         `duration_in_minutes` = duration,
		`aws_request_id`=awsRequestId 
		WHERE `participant_plan_item_id` = participantPlanItemId;
        
         
            
            
            
            if recurrencePatternRule='{}' then
				update participant_plan_item set
                recurrence_end_date=str_to_date(dueDate,'%m/%d/%Y')
                where participant_plan_item_id=participantPlanItemId;
            end if;    
        
		

        if @countOfRecurrenceId !=0 then
        
	        UPDATE `plan_item_recurrence`
			SET
			`start_time` = startTime,
			`due_date` = dueDate,
			`location` = location,
			`completion_date` = completionDate,
			`status_id` = statusid,
			`is_need_met_after_completion` = didPlanItemMeetNeedOnCompletion,
			`notes` = notes,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id` = awsRequestId
			WHERE `recurrence_id` = recurrenceId 
			AND `plan_item_id` = participantPlanItemId;
            
         else
         
				INSERT INTO `plan_item_recurrence`
