				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				VALUES
				(recurrenceId,
				participantPlanItemId,
				@recurrencePatternId,
				startTime,
				dueDate,
                completionDate,
				location,
				statusId,
				notes,
                didPlanItemMeetNeedOnCompletion,
				modifiedBy,
				CURRENT_TIMESTAMP,
				awsRequestId);


         end if;
            

            
            
	 
	end if;
    
    
    
    select count(*) into @countOfShares from pokket_user_share
    where shared_ref_id=participantPlanItemId;
    
    if @countOfShares > 0 then
   		 update pokket_user_share set is_mandatory=isMandatory, is_active=1,
            modified_by=modifiedBy, modified_dtm=CURRENT_TIMESTAMP, 
            aws_request_id=awsRequestId 
            where shared_ref_id=participantPlanItemId; 
     else
			
			
			select count(*) into @cmRoleCount from pokket_user_role
				where pokket_user_id=modifiedBy and user_role_id=4;
			if  @cmRoleCount > 0 and serviceId is not null  then 
				 call mypokket.uspInsertPokketUserShare(UUID(), pokketUserId, serviceId, participantPlanItemId, 3, 5, 1, needCategoryId,UUID(), modifiedBy,awsRequestId);
				IF ( isMandatory =1) THEN
					update pokket_user_share set is_mandatory=1, modified_by=modifiedBy, modified_dtm=current_timestamp,
					aws_request_id=awsRequestId 
					where shared_ref_id=participantPlanItemId;
				END IF;
			end if;
     
     end if;
       
    
			
            
            if (@existingNeedCategoryId != needCategoryId) then

				update pokket_user_share set is_active=0, modified_by=modifiedBy,
                modified_dtm=CURRENT_TIMESTAMP,
                aws_request_id=awsRequestId
                where shared_ref_id=participantPlanItemId
                and share_type_id=2 and need_category_id= @existingNeedCategoryId ;
                
               
            end if;
    
   
	
   COMMIT;
  
   CALL uspGetParticipantPlanItem(participantPlanItemId,recurrenceId,modifiedBy,awsRequestId);
   

END ;;
