                where participant_plan_item_id=reminderId;
            end if;    
        
		

        if @countOfRecurrenceId !=0 then
        
	        UPDATE `plan_item_recurrence`
			SET
			`due_date` = dueDate,
			`location` = location,
			`notes` = notes,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id` = awsRequestId
			WHERE `recurrence_id` = recurrenceId 
			AND `plan_item_id` = reminderId;
            
         else
         
				INSERT INTO `plan_item_recurrence`
				(`recurrence_id`,
				`plan_item_id`,
				`recurrence_pattern_id`,
				`due_date`,
				`location`,
				`status_id`,
				`notes`,
				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				VALUES
				(recurrenceId,
				reminderId,
				@recurrencePatternId,
				dueDate,
				location,
				2,
				notes,
				modifiedBy,
				CURRENT_TIMESTAMP,
				awsRequestId);


         end if;
            

            
            
	end if;
    
    
	
   COMMIT;
  
      call mypokket.uspGetParticipantReminder(reminderId, recurrenceId, modifiedBy, awsRequestId);
   
end if;

