CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdatePokketUserEvent`(
		eventId varchar(50),
        pokketUserId varchar(50),
        eventTitle varchar(50),
        eventDescription varchar(150),
        eventLocation varchar(150),
        eventTimezone varchar(50),
        eventStartDateTime DateTime,
        eventEndDateTime DateTime,
        repeatInterval tinyint(4),
        repeatTypeId varchar(10),
        inviteeIds varchar(250),
        modifiedBy varchar(50),
        awsRequestId varchar(50))
BEGIN
    DECLARE formattedInviteeIds varchar(250);
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    UPDATE `pokket_user_event`
	SET
    `event_title`=eventTitle,
	`event_description` = eventDescription,
	`event_location` = eventLocation,
	`event_tz` = eventTimezone,
	`starts_at_dtm` = eventStartDateTime,
	`ends_at_dtm` = eventEndDateTime,
    `duration` = TIMESTAMPDIFF(MINUTE,eventStartDateTime,eventEndDateTime),
	`event_repeat_interval` = repeatInterval,
	`event_repeat_type_id` = repeatTypeId,
	`modified_by` = modifiedBy,
	`modified_dtm` = CURRENT_TIMESTAMP,
    `aws_request_id`=awsRequestId 
	WHERE `event_id` = eventId;
 
    DELETE FROM `pokket_user_event_invitee` WHERE `event_id`=eventId;

        SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        
        
        select formatCSL(inviteeIds) into formattedInviteeIds; 
 		WHILE formattedInviteeIds != '' > 0 DO
			SET @inviteeId = SUBSTRING_INDEX(formattedInviteeIds, @separator, 1);
 			if @inviteeId !='0' then
				INSERT INTO `pokket_user_event_invitee`
				(`event_id`,
				`invitee_pokket_user_id`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(eventId,
				@inviteeId,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
             end if;   
			SET formattedInviteeIds = SUBSTRING(formattedInviteeIds, CHAR_LENGTH(@inviteeId) + @separatorLength + 1);
		END WHILE;   
        
     COMMIT;  
    
    CALL uspGetPokketUserEvent(eventId,pokketUserId,awsRequestId);
   

END ;;
