CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdatePokketUserStatus`(
		pokketUserId varchar(50),
        pokketUserStatusId tinyint(4),
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
	UPDATE `pokket_user`
	SET
	`pokket_user_status_id` = pokketUserStatusId,
	`modified_by` = modifiedBy,
	`modified_dtm` = CURRENT_TIMESTAMP,
    `aws_request_id`=awsRequestId 
 	WHERE `pokket_user_id` = pokketUserId;
    
    
    
    if pokketUserStatusId=5 or pokketUserStatusId=6 then
    
		update pokket_user_connection set is_active=0,modified_by=modifiedBy,
        modified_dtm=current_timestamp, aws_request_id=awsRequestId where
        pokket_user_id=pokketUserId;
        
		update pokket_user_connection set is_active=0,modified_by=modifiedBy,
        modified_dtm=current_timestamp, aws_request_id=awsRequestId where
        connection_pokket_user_id=pokketUserId;
        
        
        
        
        select count(*) into @countOfParticipantRoles from pokket_user_role where pokket_user_id = pokketUserId
        and user_role_id=5;
        
		if pokketUserStatusId = 6 and @countOfParticipantRoles > 0 then	
        
                    CREATE TEMPORARY TABLE IF NOT EXISTS tmpMessageThreads AS (
						SELECT  message_thread_id FROM pokket_user_message_thread where pokket_user_id=pokketUserId
					);
                    update pokket_user_message_thread set is_archived=1, modified_by=modifiedBy,
                    modified_dtm=current_timestamp, aws_request_id=awsRequestId
                    where message_thread_id in (
					SELECT  message_thread_id FROM tmpMessageThreads
					);
                    
                    DROP TABLE tmpMessageThreads;
        end if;
        
    end if;
    
    
    
	if pokketUserStatusId=4  then
    
		update pokket_user_connection set is_active=1,modified_by=modifiedBy,
        modified_dtm=current_timestamp, aws_request_id=awsRequestId where
        pokket_user_id=pokketUserId;
        
		update pokket_user_connection set is_active=1,modified_by=modifiedBy,
        modified_dtm=current_timestamp, aws_request_id=awsRequestId where
        connection_pokket_user_id=pokketUserId;
        
    end if;
    
   COMMIT;
   
   CALL uspGetPokketUser(pokketUserId,modifiedBy,awsRequestId);

END ;;
