		organizationServiceId varchar(50),
        caseManagerIds MEDIUMTEXT,
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
   
    DECLARE formattedCaseManagerIds MEDIUMTEXT;
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
	SELECT service_type into @serviceType from organization_service where organization_service_id=organizationServiceId;
    
    
		
        update `facility_service_case_manager` set is_active=0,modified_by=modifiedBy,
        modified_dtm=current_timestamp,aws_request_id=awsRequestId where `service_id`=organizationServiceId;
        
	if @serviceType = 'VIRTUAL' then
        
		select formatCSL(caseManagerIds) into formattedCaseManagerIds; 
 		SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
 		WHILE formattedCaseManagerIds != '' > 0 DO
			SET @caseManagerId = SUBSTRING_INDEX(formattedCaseManagerIds, @separator, 1);
            select count(*) into @countOfAssignedCm from facility_service_case_manager
            where `service_id`=organizationServiceId and pokket_user_id=@caseManagerId and
            facility_id in (select organization_facility_id from organization_facility where is_virtual=1);
                if @countOfAssignedCm = 0  then
						insert into facility_service_case_manager (facility_id,service_id,pokket_user_id,is_active,aws_request_id,
						created_by,created_dtm)
						 select organization_facility_id,organizationServiceId,@caseManagerId,1,
						 awsRequestId,modifiedBy,current_timestamp 
					 from organization_facility where is_virtual=1;
                   else
                   update facility_service_case_manager set is_active=1, modified_by=modifiedBy,
					modified_dtm=current_timestamp,aws_request_id=awsRequestId 
                    where `service_id`=organizationServiceId and pokket_user_id=@caseManagerId and
						facility_id in (select organization_facility_id from organization_facility where is_virtual=1);
                end if;
			SET formattedCaseManagerIds = SUBSTRING(formattedCaseManagerIds, CHAR_LENGTH(@caseManagerId) + @separatorLength + 1);
		END WHILE;  
        
	else 
        
         select formatCSL(caseManagerIds) into formattedCaseManagerIds; 
 		SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
 		WHILE formattedCaseManagerIds != '' > 0 DO
			SET @caseManagerId = SUBSTRING_INDEX(formattedCaseManagerIds, @separator, 1);
            select count(*) into @countOfAssignedCm from facility_service_case_manager
            where `service_id`=organizationServiceId and pokket_user_id=@caseManagerId and
            facility_id in (select facility_service.facility_id
				 from facility_service inner join facility_case_manager on
				facility_service.facility_id=facility_case_manager.facility_id where 
				`facility_case_manager`.`pokket_user_id`= @caseManagerId and
				facility_service.service_id=organizationServiceId);
                if @countOfAssignedCm = 0  then
						insert into facility_service_case_manager (facility_id,service_id,pokket_user_id,is_active,aws_request_id,
						created_by,created_dtm)
						 select facility_service.facility_id,facility_service.service_id,facility_case_manager.pokket_user_id,1,
						 awsRequestId,modifiedBy,current_timestamp 
					 from facility_service inner join facility_case_manager on
					facility_service.facility_id=facility_case_manager.facility_id where 
					`facility_case_manager`.`pokket_user_id`= @caseManagerId and
					facility_service.service_id=organizationServiceId;
                   else
                   update facility_service_case_manager set is_active=1, modified_by=modifiedBy,
					modified_dtm=current_timestamp,aws_request_id=awsRequestId 
                    where `service_id`=organizationServiceId and pokket_user_id=@caseManagerId and
						facility_id in (select facility_service.facility_id
							 from facility_service inner join facility_case_manager on
							facility_service.facility_id=facility_case_manager.facility_id where 
							`facility_case_manager`.`pokket_user_id`= @caseManagerId and
							facility_service.service_id=organizationServiceId);
                end if;
			SET formattedCaseManagerIds = SUBSTRING(formattedCaseManagerIds, CHAR_LENGTH(@caseManagerId) + @separatorLength + 1);
		END WHILE; 
    
        
    
  end if;         
		
        
        
     COMMIT;   
    
	CALL uspGetService(organizationServiceId,awsRequestId);
   

