CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpdateServiceProvider`(
		organizationId varchar(50),
        organizationName varchar(100),
        organizationDescription varchar(500),
        spInviteStatus tinyint(4),
		physicalAddressLine1 varchar(100),
        physicalAddressLine2 varchar (100),
        physicalAddressCity varchar(50),
        physicalAddressCounty varchar(100),
        physicalAddressState varchar(2),
        physicalAddressZip varchar(15),
        mailingAddressLine1 varchar(100),
        mailingAddressLine2 varchar (100),
        mailingAddressCity varchar(50),
        mailingAddressCounty varchar(100),
        mailingAddressState varchar(2),
        mailingAddressZip varchar(15),
        modifiedBy varchar(50),
        awsRequestId varchar(50),
        organizationWebsite varchar(200))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    
     UPDATE `organization`
		 SET
		`organization_name` = organizationName,
		`organization_description`=organizationDescription,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
		`aws_request_id`=awsRequestId,
        `organization_website` = organizationWebsite
		WHERE `organization_id` = organizationId;
    
    
     
    
    
    
    if spInviteStatus = 6 then
			select count(*) into @existingOnboardedOrgCount from 
			organization_onboarding
			where organization_id=organizationId;
            
            if @existingOnboardedOrgCount =0 then
            INSERT INTO `organization_onboarding`
				(`organization_onboarding_id`,
				`organization_id`,
				`onboarded_by_org_id`,
				`sp_invite_status_id`,
				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				VALUES
				(UUID(),
				organizationId,
				organizationId,
				spInviteStatus,
				modifiedBy,
				CURRENT_TIMESTAMP,
				awsRequestId);

            else
				UPDATE `organization_onboarding`
				SET 
				`sp_invite_status_id`=spInviteStatus,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId
				WHERE `organization_id` = organizationId;
            end if;
        
    end if;
    
    
    
    select count(*) into @countOfPhysicalAddress from
    organization_address WHERE 
    `organization_id` = organizationId 
	AND `address_type_id` = 1;
    
    select count(*) into @countOfMailingAddress from
    organization_address WHERE 
    `organization_id` = organizationId 
	AND `address_type_id` = 2;
    
    IF(physicalAddressLine1 IS NOT NULL) THEN
		IF @countOfPhysicalAddress !=0 THEN
			UPDATE `mypokket`.`organization_address`
			SET
			`address_line_1` = physicalAddressLine1,
			`address_line_2` = physicalAddressLine2,
			`address_city` = physicalAddressCity,
			`address_county` = physicalAddressCounty,
			`address_state` = physicalAddressState,
			`address_zip` = physicalAddressZip,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId
			WHERE `organization_id` = organizationId 
			AND `address_type_id` = 1;
        ELSE
			INSERT INTO `organization_address`
			(`organization_id`,
			`address_type_id`,
			`address_line_1`,
			`address_line_2`,
			`address_city`,
			`address_county`,
			`address_state`,
			`address_zip`,
			`created_by`,
			`created_dtm`,
			`aws_request_id`)
			VALUES
			(organizationId,
			1,
			physicalAddressLine1,
			physicalAddressLine2,
			physicalAddressCity,
			physicalAddressCounty,
			physicalAddressState,
			physicalAddressZip,
			modifiedBy,
			CURRENT_TIMESTAMP,
			awsRequestId);

        END IF;
	END IF;
    
	IF(mailingAddressLine1 IS NOT NULL) THEN
		IF @countOfMailingAddress !=0 THEN
			UPDATE `mypokket`.`organization_address`
			SET
			`address_line_1` = mailingAddressLine1,
			`address_line_2` = mailingAddressLine2,
			`address_city` = mailingAddressCity,
			`address_county` = mailingAddressCounty,
			`address_state` = mailingAddressState,
			`address_zip` = mailingAddressZip,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
			`aws_request_id`=awsRequestId
			WHERE `organization_id` = organizationId 
			AND `address_type_id` = 2;
        ELSE
			INSERT INTO `organization_address`
			(`organization_id`,
			`address_type_id`,
			`address_line_1`,
			`address_line_2`,
			`address_city`,
			`address_county`,
			`address_state`,
			`address_zip`,
			`created_by`,
			`created_dtm`,
			`aws_request_id`)
			VALUES
			(organizationId,
			2,
			mailingAddressLine1,
			mailingAddressLine2,
			mailingAddressCity,
			mailingAddressCounty,
			mailingAddressState,
			mailingAddressZip,
			modifiedBy,
			CURRENT_TIMESTAMP,
			awsRequestId);
		END IF;
	END IF;
    
   
	
   COMMIT;
   
    
	CALL uspGetOrganizationById(organizationId,null,awsRequestId);
   
  

END ;;
