CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspUpsertOnboardedOrganization`(
        organizationId varchar(50),
        organizationName varchar(100),
        organizationTypeId tinyint(4),
        spInviteStatusId tinyint(4),
        onboardedByPokketUserId varchar(50),
        createdBy varchar(50),
        awsRequestId varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    declare orgStatusId tinyint(4);
    declare isNonParticipating tinyint(1);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    set orgStatusId=1; 
    set isNonParticipating=1 ;
    
	select organization_id into @onboardingOrganizationId
    from pokket_user_organization 
    where pokket_user_id=onboardedByPokketUserId;
    
    select count(*) into @orgCount from organization
    where organization_id=organizationId;
    
     select count(*) into @onboardedOrgCount from 
    organization_onboarding
    where organization_id=organizationId;
    

    
    if @orgCount=0 then
    
		INSERT INTO `organization`
		(`organization_id`,
		`organization_name`,
		`organization_type_id`,
		`organization_status_id`,
        `is_non_participating`,
		`created_by`,
		`created_dtm`,
        `aws_request_id`)
		VALUES
		(organizationId,
		organizationName,
		organizationTypeId,
		orgStatusId, 
        isNonParticipating,
		createdBy,
		CURRENT_TIMESTAMP,
        awsRequestId);
    
		INSERT INTO `organization_onboarding`
		(`organization_id`,
		`onboarded_by_org_id`,
		`sp_invite_status_id`,
		`created_by`,
		`created_dtm`,
		`modified_by`,
		`modified_dtm`,
		`aws_request_id`)
		VALUES
		(organizationId,
		onboardingOrganizationId,
		spInviteStatusId,
		createdBy,
		CURRENT_TIMESTAMP,
		awsRequestId);

	else
		if spInviteStatusId=3 then 
			set orgStatusId=4; 
            set isNonParticipating=0;
        elseif spInviteStatusId=4 then 
			set orgStatusId=5; 
            set isNonParticipating=0;
        else
			set orgStatusId=4; 
            set isNonParticipating=1;
        end if; 
        
        update `organization` set
			`organization_status_id`=orgStatusId,
            `is_non_participating`=isNonParticipating,
            `modified_by`=createdBy,
            `modified_dtm`=CURRENT_TIMESTAMP,
            `aws_request_id`=awsRequestId
            where `organization_id`=@onboardingOrganizationId;
            
        if @onboardedOrgCount =0 then
        
			INSERT INTO `organization_onboarding`
			(`organization_id`,
			`onboarded_by_org_id`,
			`sp_invite_status_id`,
			`created_by`,
			`created_dtm`,
			`modified_by`,
			`modified_dtm`,
			`aws_request_id`)
			VALUES
			(organizationId,
			onboardingOrganizationId,
			spInviteStatusId,
			createdBy,
			CURRENT_TIMESTAMP,
			awsRequestId);
        else
			update `organization_onboarding`
			set sp_invite_status_id=spInviteStatusId,
				`modified_by`=createdBy,
				`modified_dtm`=CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId
				where `organization_id`=organizationId;
        end if;
        
        
  
	end if;
    
   
	
   COMMIT;
   
   

END ;;
